import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Button,
  Divider,
  IconButton,
  Card,
  CardHeader,
  Paper,
  Grid,
  Avatar,
} from "@material-ui/core";
import { colors } from "@material-ui/core";
import { AddRounded } from "@material-ui/icons";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData(
    "23 rue henri Martin - 92170",
    "Appartement 1A",
    "M. CETRE",
    24,
    4.0
  ),
  createData("23 rue henri Martin - 92170", "Maison", "M. Fitoussi", 24, 4.0),
  createData(
    "23 rue henri Martin - 92170",
    "Appartement 3A",
    "M. BAUMERT",
    24,
    4.0
  ),
  createData(
    "67 rue des champignons- 92170",
    "Appartement RDC",
    "M. MACRON",
    24,
    4.0
  ),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin: theme.spacing(2),
    backgroundColor: theme.palette.primary.main
  },
  content: {
    marginBottom: theme.spacing(2),
  },
  destinataire: {
    color: theme.palette.primary.main,
  },
  expediteur: {
    textAlign: "right",
  },
  centerText: {
    textAlign: "center",
  },
  pageBis: {
    backgroundColor: "#fff000",
    width: "210mm",
    height: "297mm",
    padding: 30,
  },
  paper: {
    padding: 20,
  },
}));

const ChooseBoos = () => {
  const classes = useStyles();
  const currentDate = new Date().toLocaleString("default", {
    month: "long",
    year: "numeric",
  });

  return (
    <div className={classes.root}>
      <div className={classes.content}>
        <Typography variant="h2" className={classes.centerText}>
          CHOISIT TON PATRON !
        </Typography>
      </div>

      <Grid container spacing={4}>
        {rows.map((row) => (
          <Grid item xs={4}>
            <Paper elevation={0} variant="outlined" className={classes.paper}>
              <Grid container>
                <Grid item xs={3}>
                  <Avatar>V</Avatar>
                </Grid>
                <Grid item xs={9}>
                  <Typography>{row.calories}</Typography>
                </Grid>
              </Grid>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default ChooseBoos;
