import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { makeStyles, withStyles } from "@material-ui/styles";
import {
  Modal,
  Card,
  CardHeader,
  CardContent,
  CardActions,
  Grid,
  Divider,
  Typography,
  Radio,
  RadioGroup,
  FormControlLabel,
  MenuItem,
  Button,
  colors,
  Select,
  InputLabel,
} from "@material-ui/core";
import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";
import TableSelector from "./TableSelector";
import { saveNewPedalsInDataBase } from "../../../request/pedalsAPI";
import { getStockDataBase } from "../../../request/stockAPI";

const useStyles = makeStyles((theme) => ({
  root: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    outline: "none",
    boxShadow: theme.shadows[20],
    width: "60%",
    maxHeight: "90%",
    overflowY: "auto",
    maxWidth: "100%",
  },
  actions: {
    justifyContent: "flex-end",
  },
  center: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  divider: {
    margin: 10,
  },
}));

function AddOwnerModal({ open, className, ...rest }) {
  const classes = useStyles();

  const [componentCurrent, setComponentCurrent] = useState("aop");
  const [pedalCurrent, setPedalCurrent] = useState({});

  function createProprio() {
    const timestamp = Date.now();

    firebase
      .database()
      .ref("id_user/owners/" + timestamp)
      .set({
        civility: "mme",
        lastname: "Macron",
        firstname: "Valerie",
        isSociety: false,
        socialIdentity: null,
        id: timestamp,
        isOwner: true,
      });

    onClose();
  }

  const handleChange = (event, user, keyName) => {
    event.persist();
    console.log("event change", event.target.value);
    setPedalCurrent((prevFormState) => ({
      ...prevFormState,
      components: {
        ...prevFormState.components,
        [user.value]: {
          label: user.label,
          path: `${componentCurrent}/${keyName}/${user.value}`,
          quantity: event.target.value >= 0 ? event.target.value : 0,
        },
      },
    }));
  };

  const cancelClose = () => {
    onClose();
  };

  if (!open) {
    return null;
  }

  return (
    <Modal onClose={onClose} open={open}>
      <Card {...rest} className={clsx(classes.root, className)}>
        <CardHeader title={"Ajouter un nouveau propriétaire"} />
        <Divider />
        <CardContent>
          <RadioGroup
            row
            aria-label="position"
            name="position"
            defaultValue="top"
            onChange={handleChangeParticulier}
            className={classes.gridCell}
          >
            <Typography variant="h4" className={classes.titleSection}>
              Locataire(s):
            </Typography>
            <FormControlLabel
              value={"true"}
              control={
                <Radio color="primary" checked={isParticulier === "true"} />
              }
              label="Particulier"
              labelPlacement="right"
            />
            <FormControlLabel
              value={"false"}
              control={
                <Radio color="primary" checked={isParticulier === "false"} />
              }
              label="Entreprise"
              labelPlacement="right"
            />
          </RadioGroup>
        </CardContent>
        <Divider />
        <CardActions disableSpacing>
          <Button onClick={cancelClose}>annuler</Button>
          <Button onClick={createProprio}>créer</Button>
        </CardActions>
      </Card>
    </Modal>
  );
}

AddOwnerModal.propTypes = {
  className: PropTypes.string,
  onClose: PropTypes.func,
  open: PropTypes.bool,
};

AddOwnerModal.defaultProps = {
  open: false,
  onClose: () => {},
};

export default AddOwnerModal;
