import React, { useState, useEffect } from "react";
import { makeStyles, withStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  IconButton,
  CardHeader,
  TextField,
  Radio,
  RadioGroup,
  FormControlLabel,
  Paper,
} from "@material-ui/core";
import { colors } from "@material-ui/core";
import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";
import DeleteRounded from "@material-ui/icons/DeleteRounded";
import CreateRounded from "@material-ui/icons/CreateRounded";
import Particulier from "./Components/Particulier";
import Society from "./Components/Society";
import { useUser } from "../../context/userInformations";
import AddEstateModal from "./Components/AddEstateModal";
import CardAccommodation from "./Components/CardAccomodation";
import { getAccomodationByOwner } from "request/accomodationAPI";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const CssTextField = withStyles({
  root: {
    '& label.Mui-focused': {
      color: 'green',
    },
    '& .MuiInput-underline:after': {
      borderBottomColor: 'green',
    },
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: 'red',
      },
      '&:hover fieldset': {
        borderColor: 'yellow',
      },
      '&.Mui-focused fieldset': {
        borderColor: 'green',
      },
      '&.Mui-disabled fieldset': {
        borderColor: '#90D8FF'
      }
    },
    "& label.Mui-disabled" :{
      color: '#486C7F',
    }
  },
})(TextField);

const rows = [
  createData("vanves", 159, 6.0, 24, 4.0),
  createData("paris 12", 237, 9.0, 37, 4.3),
  createData("versailles", 262, 16.0, 24, 6.0),
  createData("sainte Maxime", 305, 3.7, 67, 4.3),
  createData("val d'isÃ¨re", 356, 16.0, 49, 3.9),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  paper: {
    padding: 10,
  },
  titleBar: {
    backgroundColor: theme.palette.primary.main,
    color: colors.grey[50],
    marginBottom: 10,
    textAlign: "center",
    padding: 10,
  },
  right: {
    paddingTop: 10,
    textAlign: "right",
  },
  gridCell: {
    justifyContent: "center",
  },
  cellRight: {
    textAlign: "right",
    paddingRight: 25,
  },
  titleSection: {
    marginBottom: 2,
    marginLeft: 10,
  },
  textWhite: {
    color: colors.grey[50],
  },
  center: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  container: {
    padding: 15,
  },
  button: {
    marginRight: 20,
    marginLeft: 20,
  },
  btnAction: {
    backgroundColor: theme.palette.action.main,
    color: theme.palette.white,
  },
  containerTitle: {
    paddingTop: 10,
  },
}));

const StyledToggleButtonGroup = withStyles((theme) => ({
  grouped: {
    margin: theme.spacing(1),
    border: "none",
    background: theme.palette.white,
    padding: theme.spacing(0, 3),
    "&:not(:first-child)": {
      borderRadius: theme.shape.borderRadius,
    },
    "&:first-child": {
      borderRadius: theme.shape.borderRadius,
    },
  },
  root: {
    backgroundColor: theme.palette.background.default,
  },
}))(ToggleButtonGroup);

const Accommodation = () => {
  const classes = useStyles();
  const { userInformations } = useUser();
  const [showAddEstateModal, setShowAddEstateModal] = useState(true);
  const [component, setComponent] = useState("vanves");
  const [accommodations, setData] = useState(undefined);

  function getAccommodationsInformations() {
    getAccomodationByOwner((response) => {
      console.log("response get accommodations" , response)
      setData(response);
    });
  }

  const handleComponent = (event, newComponent) => {
    setComponent(newComponent);
  };

  function handleAddEstate() {
    setShowAddEstateModal(true);
  }

  useEffect(() => {
    getAccommodationsInformations();
  }, []);

  console.log("userInfos", userInformations);

  return (
    <div className={classes.root}>
      <Grid container>
        <Grid item lg={6} md={6} xs={6} className={classes.title}>
          <Typography variant="h1">Tes logements</Typography>
          <Typography variant="body1">
            Ici tu peux modifier les locataires des logements ainsi qu'ajouter
            un logement via le button ajouter.
          </Typography>
        </Grid>
        <Grid item lg={6} md={6} xs={6} className={classes.cellRight}>
          <Button
            variant="contained"
            color="secondary"
            onClick={handleAddEstate}
          >
            {" "}
            ajouter un logement{" "}
          </Button>
        </Grid>
      </Grid>

      <AddEstateModal
        open={showAddEstateModal}
        onClose={(pedal) => {
          setShowAddEstateModal(false);
        }}
      />

      <div>
        <StyledToggleButtonGroup
          size="small"
          value={component}
          exclusive
          onChange={handleComponent}
          aria-label="text alignment"
          className={classes.center}
        >
          {rows.map((user) => (
            <ToggleButton value={user} aria-label="left aligned">
              {user.name}
            </ToggleButton>
          ))}
        </StyledToggleButtonGroup>
      </div>
      <Grid container spacing={5}>
        {accommodations !== undefined &&
          accommodations.map((product) => (
            <Grid item xs={6}>
              <CardAccommodation accomodationInfos={product}/>
            </Grid>
          ))}
      </Grid>
    </div>
  );
};

export default Accommodation;
