import Firebase from "firebase";

export const saveNewIndexAPI = (value, name) => {
  Firebase.database()
    .ref("indexes/" + name)
    .set({
      value,
    });
};

export const updateIndexesAPI = (value) => {
  Firebase.database()
    .ref("indexes/")
    .set(value);
  console.log("DATA SAVED");
};

export const getIndexesAPI = (callback) => {
  let ref = Firebase.database().ref("/indexes");
  ref.on("value", (snapshot) => {
    const data = snapshot.val();
    callback(data);
  });
};

export const updateTemplateAPI = (id,value) => {
  Firebase.database()
    .ref("templates/" + id)
    .set(value);
};

export const getTemplates = (callback) => {
  let ref = Firebase.database().ref("/templates");
  ref.on("value", (snapshot) => {
    const data = snapshot.val();
    callback(data);
  });
};

export const getTemplateById = (id, callback) => {
  let ref = Firebase.database().ref("/templates/" + id);
  ref.on("value", (snapshot) => {
    const data = snapshot.val();
    callback(data);
  });
};
