import React, { useState, useEffect } from "react";
import { makeStyles, withStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  IconButton,
  CardHeader,
  TextField,
  Radio,
  RadioGroup,
  FormControlLabel,
  Paper,
} from "@material-ui/core";
import { colors } from "@material-ui/core";
import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";
import DeleteRounded from "@material-ui/icons/DeleteRounded";
import CreateRounded from "@material-ui/icons/CreateRounded";
import Particulier from "./Components/Particulier";
import Society from "./Components/Society";
import { useUser } from "../../context/userInformations";
import AddEstateModal from "./Components/AddEstateModal";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData("vanves", 159, 6.0, 24, 4.0),
  createData("paris 12", 237, 9.0, 37, 4.3),
  createData("versailles", 262, 16.0, 24, 6.0),
  createData("sainte Maxime", 305, 3.7, 67, 4.3),
  createData("val d'isÃ¨re", 356, 16.0, 49, 3.9),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  paper: {
    padding: 10,
  },
  titleBar: {
    backgroundColor: theme.palette.primary.main,
    color: colors.grey[50],
    marginBottom: 10,
    textAlign: "center",
    padding: 10,
  },
  right: {
    textAlign: "right",
  },
  gridCell: {
    alignItems: "center",
  },
  cellRight: {
    textAlign: "right",
    paddingRight: 25,
  },
  titleSection: {
    marginBottom: 2,
    marginLeft: 20
  },
  textWhite: {
    color: colors.grey[50],
  },
  center: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  container: {
    padding: 15,
  },
  button:{
    margin: 20
  }
}));

const StyledToggleButtonGroup = withStyles((theme) => ({
  grouped: {
    margin: theme.spacing(1),
    border: "none",
    background: theme.palette.white,
    padding: theme.spacing(0, 3),
    "&:not(:first-child)": {
      borderRadius: theme.shape.borderRadius,
    },
    "&:first-child": {
      borderRadius: theme.shape.borderRadius,
    },
  },
  root: {
    backgroundColor: theme.palette.background.default,
  },
}))(ToggleButtonGroup);

const Accommodation = () => {
  const classes = useStyles();
  const { userInformations } = useUser();
  const [showAddEstateModal, setShowAddEstateModal] = useState(false);
  const [isCommercialLocal, setCommercialLocal] = useState("true");
  const [isParticulier, setParticulier] = useState("true");
  const [component, setComponent] = useState("vanves");

  const handleChangeCommercialLocal = (event) => {
    setCommercialLocal(event.target.value);
  };

  const handleChangeParticulier = (event) => {
    setParticulier(event.target.value);
  };

  const handleComponent = (event, newComponent) => {
    setComponent(newComponent);
  };

  function handleAddEstate() {
    setShowAddEstateModal(true);
  }

  console.log("userInfos", userInformations);

  return (
    <div className={classes.root}>
      <Grid container>
        <Grid item lg={6} md={6} xs={6} className={classes.title}>
          <Typography variant="h1">Tes logements</Typography>
          <Typography variant="body1">
            Ici tu peux modifier les locataires des logements ainsi qu'ajouter
            un logement via le button ajouter.
          </Typography>
        </Grid>
        <Grid item lg={6} md={6} xs={6} className={classes.cellRight}>
          <Button
            variant="contained"
            color="secondary"
            onClick={handleAddEstate}
          >
            {" "}
            ajouter un logement{" "}
          </Button>
        </Grid>
      </Grid>

      <AddEstateModal
        open={showAddEstateModal}
        onClose={(pedal) => {
          setShowAddEstateModal(false);
        }}
      />

      <div>
        <StyledToggleButtonGroup
          size="small"
          value={component}
          exclusive
          onChange={handleComponent}
          aria-label="text alignment"
          className={classes.center}
        >
          {rows.map((user) => (
            <ToggleButton value={user} aria-label="left aligned">
              {user.name}
            </ToggleButton>
          ))}
        </StyledToggleButtonGroup>
      </div>
      <Grid container spacing={5}>
        {rows !== null &&
          rows.map((product) => (
            <Grid item key={product.name} lg={6} md={6} xs={12}>
              <Paper variant="outlined" className={classes.container}>
                <Paper elvation={0} className={classes.titleBar}>
                  <Typography variant="h3" className={classes.textWhite}>
                    23 RUE HENRI MARTIN - 92170 VANVES
                  </Typography>

                  <Typography variant="h4" className={classes.textWhite}>
                    Appartement 1A
                  </Typography>
                </Paper>
                <Paper elevation={0}>
                  <RadioGroup
                    row
                    aria-label="position"
                    name="position"
                    defaultValue="top"
                    onChange={handleChangeCommercialLocal}
                    className={classes.center}
                  >
                    <FormControlLabel
                      value={"false"}
                      control={
                        <Radio
                          color="secondary"
                          checked={isCommercialLocal === "false"}
                        />
                      }
                      label="Habitation"
                      labelPlacement="right"
                    />
                    <FormControlLabel
                      value={"true"}
                      control={
                        <Radio
                          color="secondary"
                          checked={isCommercialLocal === "true"}
                        />
                      }
                      label="Local Commercial"
                      labelPlacement="right"
                    />
                  </RadioGroup>

                  <Typography variant="h4" className={classes.titleSection}>
                    Loyer
                  </Typography>

                  <Paper elevation={0} className={classes.paper}>
                    <Grid container spacing={2}>
                      <Grid item lg={4} md={4} xs={4}>
                        <TextField
                          size="small"
                          label="Fixe"
                          variant="outlined"
                          fullWidth
                        />
                      </Grid>

                      <Grid item lg={4} md={4} xs={4}>
                        <TextField
                          size="small"
                          label="Charges"
                          variant="outlined"
                          fullWidth
                        />
                      </Grid>
                      {isCommercialLocal == "true" && (
                        <Grid item lg={4} md={4} xs={4}>
                          <TextField
                            size="small"
                            label="TVA en %"
                            variant="outlined"
                            fullWidth
                          />
                        </Grid>
                      )}

                      <Grid item xs={6}>
                        <Typography variant="overline">
                          Indice Insee de ref :T1
                        </Typography>
                      </Grid>

                      <Grid
                        item
                        lg={6}
                        md={6}
                        xs={6}
                        className={classes.cellRight}
                      >
                        <Typography variant="h5"> Total : 3000 </Typography>
                      </Grid>
                    </Grid>
                  </Paper>

                    <Typography variant="h4" className={classes.titleSection}>
                      Locataire
                    </Typography>
                    <Typography variant="overline" className={classes.titleSection}>
