import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import { Typography, Button } from "@material-ui/core";
import {html2canvas} from "html2canvas";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin : theme.spacing(2)
  },
  content: {
    marginTop: theme.spacing(2),
  },
}));

const Receipt = () => {
  const classes = useStyles();

  function createPDF() {
    console.log("create")

        const input = document.getElementById('divToPrint');
        html2canvas(input)
          .then((canvas) => {
            const imgData = canvas.toDataURL('image/png');
            const pdf = new jsPDF();
            pdf.addImage(imgData, 'JPEG', 0, 0);
            // pdf.output('dataurlnewwindow');
            pdf.save("download.pdf");
          })
        ;

  }

  function downloadPDF() {
      console.log("download PDF")
  }


  return (
    <div className={classes.root}>
 <h1>HELLO ON VA FAIRE DES QUITTANCES</h1>
        <Button className={classes.root} variant="contained" onClick={createPDF()}>CREATE PDF</Button>
        <Button className={classes.root}variant="contained" onClick={downloadPDF()}>Download PDF</Button>

    </div>
  );
};

export default Receipt;
