import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  Avatar,
  CardHeader,
  TextField,
  Divider,
  Radio,
RadioGroup,
  FormControlLabel,
} from "@material-ui/core";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData("Frozen yoghurt", 159, 6.0, 24, 4.0),
  createData("Ice cream sandwich", 237, 9.0, 37, 4.3),
  createData("Eclair", 262, 16.0, 24, 6.0),
  createData("Cupcake", 305, 3.7, 67, 4.3),
  createData("Gingerbread", 356, 16.0, 49, 3.9),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  loyerBox: {
    marginLeft: 12,
  },
  actionBar: {
    backgroundColor: "#FFF675",
  },
  gridCell: {
    alignItems: "center",
  },
  divider: {
    margin: theme.spacing(2, 0),
    backgroundColor: "#FFF000",
  },
}));

const Accommodation = () => {
  const classes = useStyles();
  const [isCommercialLocal, setCommercialLocal] = React.useState(0);

  const handleChange = (event) => {
    setCommercialLocal(event.target.value);
  };

  return (
    <div className={classes.root}>
      <Typography variant="h1">VOICI TES LOGEMENTS</Typography>

      <Grid container spacing={3}>
        {rows !== null &&
          rows.map((product) => (
            <Grid item key={product.name} lg={4} md={6} xs={12}>
              <Card variant="contained">
                <CardHeader
                  avatar={
                    <Avatar aria-label="recipe" className={classes.avatar}>
                      V
                    </Avatar>
                  }
                  title="23 RUE HENRI MARTIN - 92170 VANVES"
                  subheader="Appartement 1A"
                />
                <CardContent>
                  <Grid container spacing={1}>
                    <Grid
                      item
                      lg={12}
                      md={12}
                      xs={12}
                      className={classes.gridCell}
                    >
                      <RadioGroup
                        row
                        aria-label="position"
                        name="position"
                        defaultValue="top"
                        onChange={handleChange}
                      >
                        <FormControlLabel
                          value="top"
                          control={<Radio color="primary" checked={isCommercialLocal === 'top'} />}
                          label="local Commercial"
                          labelPlacement="right"
                        />
                        <FormControlLabel
                          value="left"
                          control={<Radio color="primary" checked={isCommercialLocal === 'left'} />}
                          label="Habitation"
                          labelPlacement="right"
                        />
                      </RadioGroup>
                    </Grid>

                    <Grid
                      item
                      lg={6}
                      md={6}
                      xs={6}
                      className={classes.gridCell}
                    >
                      <Typography variant="h6">Locataire(s):</Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <TextField
                        size="small"
                        label="Outlined"
                        variant="outlined"
                      />
                    </Grid>

                    <Divider className={classes.divider} />

                    <Grid item lg={12} md={12} xs={12}>
                      <Typography variant="h6">Loyer</Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <Typography className={classes.loyerBox}>
                        Fixe:
                      </Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <TextField
                        size="small"
                        label="Outlined"
                        variant="outlined"
                      />
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <Typography className={classes.loyerBox}>
                        Charge:
                      </Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <TextField
                        size="small"
                        label="Outlined"
                        variant="outlined"
                      />
                    </Grid>
                  </Grid>
                </CardContent>
                <CardActions className={classes.actionBar}>
                  <Button size="small">Supprimer</Button>
                  <Button size="small">Modifier</Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
      </Grid>
    </div>
  );
};

export default Accommodation;
