import React, { Component, useState } from "react";
import { Router } from "react-router-dom";
import { createBrowserHistory } from "history";
import { Chart } from "react-chartjs-2";
import { ThemeProvider } from "@material-ui/styles";
import validate from "validate.js";

import { chartjs } from "./helpers";
import theme from "./theme";
import "react-perfect-scrollbar/dist/css/styles.css";
import "./assets/scss/index.scss";
import validators from "./common/validators";
import Routes from "./Routes";
import firebase from "firebase/app";
import config from "./components/Firebase/config";
import { AuthContext } from "./context/auth";
import { UserContext } from "context/userInformations";

const browserHistory = createBrowserHistory();

Chart.helpers.extend(Chart.elements.Rectangle.prototype, {
  draw: chartjs.draw,
});

validate.validators = {
  ...validate.validators,
  ...validators,
};

const App = (props) => {
  const existingTokens = JSON.parse(localStorage.getItem("tokens"));
  const [authTokens, setAuthTokens] = useState(existingTokens);

  const existingUser = JSON.parse(localStorage.getItem("owner"));
  const [userInformations, setUserInformations] = useState(existingUser);

  const setTokens = (data) => {
    localStorage.setItem("tokens", JSON.stringify(data));


    setAuthTokens(data);
  };

  const setUser = (data) => {
    localStorage.setItem("owner", JSON.stringify(data));
    setAuthTokens(data);
  };

  if (firebase.apps.length === 0) {
    firebase.initializeApp(config);
  }

  function checkIfAvailableToken(token) {
    firebase
    .auth()
    .signInWithCustomToken(token)
    .then((userCredential) => {
      // Signed in
      var user = userCredential.user;
      console.log("user", user);
    })
    .catch((error) => {
      var errorCode = error.code;
      var errorMessage = error.message;
      console.log("error", error);
    });
  }

  return (
    <ThemeProvider theme={theme}>
      <AuthContext.Provider value={{ authTokens, setAuthTokens: setTokens }}>
        <UserContext.Provider
          value={{ userInformations, setUserInformations: setUser }}
        >
          <Router history={browserHistory}>
            <Routes />
          </Router>
        </UserContext.Provider>
      </AuthContext.Provider>
    </ThemeProvider>
  );
};

export default App;
