import React, { useState, useEffect } from "react";
import { makeStyles, withStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  IconButton,
  CardHeader,
  TextField,
  Radio,
  RadioGroup,
  FormControlLabel,
  Paper,
} from "@material-ui/core";
import { colors } from "@material-ui/core";
import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";
import DeleteRounded from "@material-ui/icons/DeleteRounded";
import CreateRounded from "@material-ui/icons/CreateRounded";
import Particulier from "./Components/Particulier";
import Society from "./Components/Society";
import { useUser } from "../../context/userInformations";
import AddEstateModal from "./Components/AddEstateModal";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const CssTextField = withStyles({
  root: {
    "& label.Mui-focused": {
      color: "green",
    },
    "& .MuiInput-underline:after": {
      borderBottomColor: "green",
    },
    "& .MuiOutlinedInput-root": {
      "& fieldset": {
        borderColor: "red",
      },
      "&:hover fieldset": {
        borderColor: "yellow",
      },
      "&.Mui-focused fieldset": {
        borderColor: "green",
      },
      "&.Mui-disabled fieldset": {
        borderColor: "#90D8FF",
      },
    },
    "& label.Mui-disabled": {
      color: "#486C7F",
    },
  },
})(TextField);

const rows = [
  createData("vanves", 159, 6.0, 24, 4.0),
  createData("paris 12", 237, 9.0, 37, 4.3),
  createData("versailles", 262, 16.0, 24, 6.0),
  createData("sainte Maxime", 305, 3.7, 67, 4.3),
  createData("val d'isÃ¨re", 356, 16.0, 49, 3.9),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  paper: {
    padding: 10,
  },
  titleBar: {
    backgroundColor: theme.palette.primary.main,
    color: colors.grey[50],
    marginBottom: 10,
    textAlign: "center",
    padding: 10,
  },
  right: {
    paddingTop: 10,
    textAlign: "right",
  },
  gridCell: {
    justifyContent: "center",
  },
  cellRight: {
    textAlign: "right",
    paddingRight: 25,
  },
  titleSection: {
    marginBottom: 2,
    marginLeft: 10,
  },
  textWhite: {
    color: colors.grey[50],
  },
  center: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  container: {
    padding: 15,
  },
  button: {
    marginRight: 20,
    marginLeft: 20,
  },
  btnAction: {
    backgroundColor: theme.palette.action.main,
    color: theme.palette.white,
  },
  containerTitle: {
    paddingTop: 10,
  },
}));

const CardAccommodation = (accomodationInfos) => {
  const classes = useStyles();
  const { userInformations } = useUser();
  const [showAddEstateModal, setShowAddEstateModal] = useState(false);
  const [isCommercialLocal, setCommercialLocal] = useState("true");
  const [isParticulier, setParticulier] = useState("true");
  const [component, setComponent] = useState("vanves");
  const [isModifying, setModify] = useState(false);

  const handleChangeCommercialLocal = (event) => {
    setCommercialLocal(event.target.value);
  };

  const handleChangeParticulier = (event) => {
    setParticulier(event.target.value);
  };

  const handleComponent = (event, newComponent) => {
    setComponent(newComponent);
  };

  function handleAddEstate() {
    setShowAddEstateModal(true);
  }

  function handleModify() {
    setModify((prev) => !prev);
  }


  return (
      <Paper variant="outlined" className={classes.container}>
        <Paper elvation={0} className={classes.titleBar}>
          <Typography variant="h3" className={classes.textWhite}>
            23 RUE HENRI MARTIN - 92170 VANVES
          </Typography>

          <Typography variant="h4" className={classes.textWhite}>
            Appartement 1A
          </Typography>
        </Paper>
        <Paper elevation={0}>
          <RadioGroup
            row
            aria-label="position"
            name="position"
            defaultValue="top"
            onChange={handleChangeCommercialLocal}
            className={classes.center}
          >
            <FormControlLabel
              value={"false"}
              control={
                <Radio
                  color="secondary"
                  checked={isCommercialLocal === "false"}
                />
              }
              label="Habitation"
              labelPlacement="right"
              disabled={!isModifying}
            />
            <FormControlLabel
              value={"true"}
              control={
                <Radio
                  color="secondary"
                  checked={isCommercialLocal === "true"}
                />
              }
              label="Local Commercial"
              disabled={!isModifying}
              labelPlacement="right"
            />
          </RadioGroup>

          <Typography variant="h4" className={classes.titleSection}>
            Loyer
          </Typography>
          <Typography variant="overline" className={classes.titleSection}>
            Indice Insee de ref :T1
          </Typography>

          <Paper elevation={0} className={classes.paper}>
            <Grid container spacing={2}>
              <Grid item lg={3} md={3} xs={3}>
                <TextField
                  size="small"
                  label="Fixe"
                  variant="outlined"
                  fullWidth
                  disabled={!isModifying}
                />
              </Grid>

              <Grid item lg={3} md={3} xs={3}>
                <TextField
                  size="small"
                  label="Charges"
                  variant="outlined"
                  fullWidth
                  disabled={!isModifying}
                />
              </Grid>
              {isCommercialLocal == "true" && (
                <Grid item lg={3} md={3} xs={3}>
                  <TextField
                    size="small"
                    label="TVA en %"
                    variant="outlined"
                    fullWidth
                    disabled={!isModifying}
                  />
                </Grid>
              )}

              <Grid item lg={3} md={3} xs={3} className={classes.gridCell}>
                <CssTextField
                  size="small"
                  label="Total"
                  variant="outlined"
                  fullWidth
                  value={3000}
                  disabled
                  inputProps={{ "aria-label": "naked" }}
                />
              </Grid>
            </Grid>
          </Paper>

          <Grid container spacing={1} className={classes.containerTitle}>
            <Grid item className={classes.gridCell}>
              <div className={classes.containerTitle}>
                <Typography variant="h4" className={classes.titleSection}>
                  Locataire
                </Typography>
                <Typography variant="overline" className={classes.titleSection}>
                  Date d'entrÃ©e : 12/01/2020
                </Typography>
              </div>
            </Grid>
            <Grid item className={classes.gridCell}>
              <RadioGroup
                row
                aria-label="position"
                name="position"
                defaultValue="top"
                onChange={handleChangeParticulier}
                className={classes.paper}
              >
                <FormControlLabel
                  value={"true"}
                  control={
                    <Radio
                      color="secondary"
                      checked={isParticulier === "true"}
                    />
                  }
                  label="Particulier"
                  disabled={!isModifying}
                  labelPlacement="right"
                />
                <FormControlLabel
                  value={"false"}
                  control={
                    <Radio
                      color="secondary"
                      checked={isParticulier === "false"}
                    />
                  }
                  label="Entreprise"
                  labelPlacement="right"
                  disabled={!isModifying}
                />
              </RadioGroup>
            </Grid>
          </Grid>

          {isParticulier == "true" && (
            <Paper elevation={0} className={classes.paper}>
              <Particulier disabled={isModifying} />
            </Paper>
          )}

          {isParticulier == "false" && (
            <Paper elevation={0} className={classes.paper}>
              <Society disabled={isModifying} />
            </Paper>
          )}
        </Paper>

        <Paper elevation={0} className={classes.paper}>
          <div className={classes.right}>
            <Button className={classes.button}>Supprimer</Button>
            <Button
              variant="contained"
              className={classes.btnAction}
              startIcon={<CreateRounded />}
              onClick={handleModify}
            >
              {!isModifying && "Modifier"}
              {isModifying && "Sauvegarder"}
            </Button>
          </div>
        </Paper>
      </Paper>
  );
};

export default CardAccommodation;
