import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { makeStyles, withStyles } from "@material-ui/styles";
import {
  Card,
  CardContent,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  TableContainer,
  CardHeader,
  TextField,
} from "@material-ui/core";
import EditRounded from "@material-ui/icons/EditRounded";
import SaveRounded from "@material-ui/icons/SaveRounded";

const useStyles = makeStyles((theme) => ({
  root: {},
  row: {
    height: "42px",
    display: "flex",
    alignItems: "center",
    marginTop: theme.spacing(1),
  },
  spacer: {
    flexGrow: 1,
  },
  importButton: {
    marginRight: theme.spacing(1),
  },
  exportButton: {
    marginRight: theme.spacing(1),
  },
  searchInput: {
    marginRight: theme.spacing(1),
  },
  container: {
    maxHeight: 740,
  },
  headerTitle: {
    paddingBottom: 0,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
}));

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.background.default,
    },
  },
}))(TableRow);

const StockInfo = (props) => {
  const { className, groupComponents, nameGroup, ...rest } = props;

  const classes = useStyles();

  const [modify, setModify] = useState(false);
  const [stockComponents, setStockComponents] = useState(groupComponents);

  function handleModifyStock(event) {
    event.persist();
    console.log("key", event.target.key);

    setStockComponents((prevFormState) => ({
      ...prevFormState,
      [event.target.id]: {
        ...prevFormState[event.target.id],
        stock: event.target.value,
      },
    }));
  }

  function handleModify() {
    console.log("components", groupComponents);
    setModify(true);
  }

  function handleSave() {
    console.log("newStock", stockComponents);
    setModify(false);
  }

  return (
    <Card {...rest} className={clsx(classes.root, className)}>
      <CardHeader
        title={nameGroup}
        style={{ textAlign: "center" }}
        action={
          <>
            {!modify && (
              <IconButton aria-label="settings" onClick={handleModify}>
                <EditRounded />
              </IconButton>
            )}
            {modify && (
              <IconButton aria-label="settings" onClick={handleSave}>
                <SaveRounded />
              </IconButton>
            )}
          </>
        }
      />
      <CardContent className={classes.content}>
        <TableContainer className={classes.container}>
          <Table stickyHeader size="small">
            <TableHead>
              <TableRow>
                <TableCell>Valeur</TableCell>
                <TableCell>Stock</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {groupComponents.map((user, index) => (
                <StyledTableRow>
                  <StyledTableCell>{user.label}</StyledTableCell>
                  <StyledTableCell>
                    <TextField
                      variant="outlined"
                      size="small"
                      type="number"
                      id={index}
                      key={index}
                      onChange={handleModifyStock}
                      disabled={!modify}
                    />
                  </StyledTableCell>
                </StyledTableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </CardContent>
    </Card>
  );
};

StockInfo.propTypes = {
  className: PropTypes.string,
  groupComponents: PropTypes.object,
};

export default StockInfo;
