import React, { useState, useEffect } from "react";
import { Link as RouterLink } from "react-router-dom";
import clsx from "clsx";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/styles";
import {
  AppBar,
  Toolbar,
  Select,
  InputLabel,
  MenuItem,
  FormControl,
  Grid,
} from "@material-ui/core";
import { getOwnerDataBase } from "request/ownerAPI";
import { useUser } from "context/userInformations";

const useStyles = makeStyles((theme) => ({
  root: {
    boxShadow: "none",
    backgroundColor: theme.palette.secondary.main,
  },
  flexGrow: {
    flexGrow: 1,
  },
  signOutButton: {
    marginLeft: theme.spacing(1),
  },
  title: {
    color: theme.palette.white,
    fontSize: 20,
    maxHeight: 40,
  },
  rootLink: {
    display: "flex",
    alignItems: "center",
    textAlign: "center",
    padding: 10,
  },
  rightSide: {
    textAlign: "right",
    padding: 5,
  },
  formControl: {
    minWidth: 200,
    paddingTop: 10,
    paddingRight: 40,
    borderColor: theme.palette.secondary.main,
  },
}));

const Topbar = (props) => {
  const { className, onSidebarOpen, ...rest } = props;
  const classes = useStyles();
  const { setUserInformations, userInformations } = useUser();
  const [owners, setData] = useState(null);

  function getBossInformations() {
    getOwnerDataBase((response) => {
      console.log("response get owner", response);
      setData(response);
    });
  }

  function handleChangeUser(event)  {
    console.log("user", event.target.value)

    var currentOwner = Object.values(owners).find((user) => user.id = event.target.value)
    setUserInformations(currentOwner);
  };

  useEffect(() => {
    getBossInformations();
  }, []);

  return (
    <AppBar {...rest} className={clsx(classes.root, className)}>
      <Toolbar>
        <Grid container>
          <Grid item xs={6}>
            <RouterLink className={classes.rootLink} to="/">
              <img
                alt="Logo"
                src="/images/logos/logo_houses_white.svg"
                width="200"
              />
            </RouterLink>
          </Grid>
          <Grid item xs={6} className={classes.rightSide}>
            <FormControl variant="outlined" className={classes.formControl}>
              <Select
                labelId="demo-simple-select-outlined-label"
                id="demo-simple-select-outlined"
                fullWidth
                label="Age"
                value={userInformations.id}
                onChange={handleChangeUser}
                autoWidth={false}
                className={classes.title}
              >
                {owners !== null &&
                  Object.values(owners).map((owner) => (
                    <MenuItem value={owner.id}>
                      {!owner.isParticulier ? owner.firstname + " " + owner.lastname : owner.socialIdentity} 
                    </MenuItem>
                  ))}

              </Select>
            </FormControl>
          </Grid>
        </Grid>
      </Toolbar>
    </AppBar>
  );
};

Topbar.propTypes = {
  className: PropTypes.string,
  onSidebarOpen: PropTypes.func,
};

export default Topbar;
