import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";

import { Divider, Typography } from "@material-ui/core";

const lastDay = {
  janvier: 31,
  fevrier: 28,
  mars: 31,
  avril: 30,
  mai: 31,
  juin: 30,
  juillet: 31,
  aout: 31,
  septembre: 30,
  octobre: 31,
  novembre: 30,
  decembe: 31,
};

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin: theme.spacing(2),
  },
  content: {
    marginBottom: theme.spacing(2),
  },
  destinataire: {
    color: theme.palette.primary.main,
  },
  expediteur: {
    textAlign: "right",
  },
  page: {
    position: "absolute",
    opacity: 0,
    pointerEvents: "none",
  },
  pageBis: {
    backgroundColor: "#DCF3FF",
    width: "210mm",
    height: "297mm",
    padding: 30,
  },
  divider: {
    margin: 20,
  },
  test: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
}));

const Template = ({ owner, accomodation, date, ...rest }) => {
  const classes = useStyles();

  return (
    <div id="divToPrint">
      <div class="divToPrint ">
        <div className={classes.pageBis}>
          <div className={classes.destinataire}>
            <Typography>
              {!owner.isSociety
                ? owner.firstname.toUpperCase() +
                  " " +
                  owner.lastname.toUpperCase()
                : owner.socialIdentity}
            </Typography>
            <Typography>{owner.address.street.toUpperCase()}</Typography>
            <Typography>
              {owner.address.postalCode.toUpperCase() +
                " " +
                owner.address.city.toUpperCase()}
            </Typography>
          </div>
          <div className={classes.expediteur}>
            {
              <Typography>
                {" "}
                {accomodation.rental.isParticulier
                  ? accomodation.rental.firstname.toUpperCase() +
                    " " +
                    accomodation.rental.lastname.toUpperCase()
                  : accomodation.rental.socialIdentity}
              </Typography>
            }
            <Typography>{accomodation.address.street.toUpperCase()}</Typography>
            <Typography>
              {accomodation.address.postalCode.toUpperCase() +
                " " +
                accomodation.address.city.toUpperCase()}
            </Typography>
          </div>

          <div>
            <h1>QUITTANCE DE LOYER</h1>
            <Divider className={classes.divider} />
          </div>
          <div>
            <p>Mademoiselle,</p>
            <Divider className={classes.divider} />
            <p>
              Je vous remercie d'avoir effectué le versement du montant de votre
              loyer pour la periode du 1er au {lastDay[date.month]} {date.month} pour les locaaux que vous
              occupez sis à {accomodation.address.city.toUpperCase()}, {accomodation.address.street.toUpperCase()}, et dont le detail est cidessous:
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Template;
