import React, { useState, useEffect } from "react";
import { makeStyles, withStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  IconButton,
  CardHeader,
  TextField,
  Radio,
  RadioGroup,
  FormControlLabel,
  Paper,
} from "@material-ui/core";
import { colors } from "@material-ui/core";
import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";
import DeleteRounded from "@material-ui/icons/DeleteRounded";
import CreateRounded from "@material-ui/icons/CreateRounded";
import Particulier from "./Components/Particulier";
import Society from "./Components/Society";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData("vanves", 159, 6.0, 24, 4.0),
  createData("paris 12", 237, 9.0, 37, 4.3),
  createData("versailles", 262, 16.0, 24, 6.0),
  createData("sainte Maxime", 305, 3.7, 67, 4.3),
  createData("val d'isÃ¨re", 356, 16.0, 49, 3.9),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  paper: {
    padding: 10,
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    marginBottom: 20,
  },
  loyerBox: {
    marginLeft: 12,
  },
  actionBar: {
    backgroundColor: "#A6A6A6",
  },
  titleBar: {
    backgroundColor: theme.palette.primary.main,
    color: colors.grey[50],
  },
  gridCell: {
    alignItems: "center",
  },
  cellRight: {
    textAlign: "right",
    paddingRight: 25,
  },
  titleSection: {
    marginBottom: 2,
    marginRight: 5,
    marginLeft: 10,
  },
  divider: {
    margin: 5,
    height: 2,
    width: "100%",
  },
  textWhite: {
    color: colors.grey[50],
  },
  center: {
    marginTop: 10,
    marginBottom: 20,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
}));

const StyledToggleButtonGroup = withStyles((theme) => ({
  grouped: {
    margin: theme.spacing(1),
    border: "none",
    background: theme.palette.white,
    padding: theme.spacing(0, 3),
    "&:not(:first-child)": {
      borderRadius: theme.shape.borderRadius,
    },
    "&:first-child": {
      borderRadius: theme.shape.borderRadius,
    },
  },
  root: {
    backgroundColor: theme.palette.background.default,
  },
}))(ToggleButtonGroup);

const Accommodation = () => {
  const classes = useStyles();
  const [isCommercialLocal, setCommercialLocal] = React.useState("true");
  const [isParticulier, setParticulier] = React.useState("true");
  const [component, setComponent] = useState("vanves");

  const handleChangeCommercialLocal = (event) => {
    setCommercialLocal(event.target.value);
  };

  const handleChangeParticulier = (event) => {
    setParticulier(event.target.value);
  };

  const handleComponent = (event, newComponent) => {
    setComponent(newComponent);
  };

  return (
    <div className={classes.root}>
      <Grid container>
        <Grid item lg={6} md={6} xs={6} className={classes.title}>
          <Typography variant="h1">Tes logements</Typography>
          <Typography variant="body1">
            Ici tu peux modifier les locataires des logements ainsi qu'ajouter
            un logement via le button ajouter.
          </Typography>
        </Grid>
        <Grid item lg={6} md={6} xs={6} className={classes.cellRight}>
            <Button variant="contained" color="secondary"> ajouter un logement </Button>
        </Grid>
      </Grid>

      <div>
        <StyledToggleButtonGroup
          size="small"
          value={component}
          exclusive
          onChange={handleComponent}
          aria-label="text alignment"
          className={classes.center}
        >
          {rows.map((user) => (
            <ToggleButton value={user} aria-label="left aligned">
              {user.name}
            </ToggleButton>
          ))}
        </StyledToggleButtonGroup>
      </div>
      <Grid container spacing={2}>
          <Grid item xs={2}>


          </Grid>
          <Grid item xs={10}>
      <Grid container spacing={5}>
        {rows !== null &&
          rows.map((product) => (
            <Grid item key={product.name} lg={6} md={6} xs={12}>
              <Card variant="contained">
                <CardHeader
                  className={classes.titleBar}
                  title={
                    <Typography variant="h3" className={classes.textWhite}>
                      {" "}
                      23 RUE HENRI MARTIN - 92170 VANVES
                    </Typography>
                  }
                  subheader={
                    <Typography variant="h4" className={classes.textWhite}>
                      {" "}
                      Appartement 1A
                    </Typography>
                  }
                  action={
                    <div>
                      <IconButton aria-label="settings">
                        <DeleteRounded
                          fontSize="large"
                          style={{ color: colors.grey[50] }}
                        />
                      </IconButton>
                      <IconButton aria-label="settings">
                        <CreateRounded
                          fontSize="large"
                          style={{ color: colors.grey[50] }}
                        />
                      </IconButton>
                    </div>
                  }
                />
                <CardContent>
                  <Grid container spacing={1}>
                    <Grid
                      item
                      lg={6}
                      md={6}
                      xs={6}
                      className={classes.gridCell}
                    >
                      <RadioGroup
                        row
                        aria-label="position"
                        name="position"
                        defaultValue="top"
                        onChange={handleChangeCommercialLocal}
                      >
                        <FormControlLabel
                          value={"true"}
                          control={
                            <Radio
                              color="primary"
                              checked={isCommercialLocal === "true"}
                            />
                          }
                          label="Local Commercial"
                          labelPlacement="right"
                        />
                        <FormControlLabel
                          value={"false"}
                          control={
                            <Radio
                              color="primary"
                              checked={isCommercialLocal === "false"}
                            />
                          }
                          label="Habitation"
                          labelPlacement="right"
                        />
                      </RadioGroup>
                    </Grid>

                    <Grid
                      item
                      lg={12}
                      md={12}
                      xs={12}
                      className={classes.gridCell}
                    >
                      <RadioGroup
                        row
                        aria-label="position"
                        name="position"
                        defaultValue="top"
                        onChange={handleChangeParticulier}
                        className={classes.gridCell}
                      >
                        <Typography
                          variant="h4"
                          className={classes.titleSection}
                        >
                          Locataire(s):
                        </Typography>
                        <FormControlLabel
                          value={"true"}
                          control={
                            <Radio
                              color="primary"
                              checked={isParticulier === "true"}
                            />
                          }
                          label="Particulier"
                          labelPlacement="right"
                        />
                        <FormControlLabel
                          value={"false"}
                          control={
                            <Radio
                              color="primary"
                              checked={isParticulier === "false"}
                            />
                          }
                          label="Entreprise"
                          labelPlacement="right"
                        />
                      </RadioGroup>

                      {isParticulier == "true" && (
                        <Paper className={classes.paper}>
                          <Particulier />
                        </Paper>
                      )}

                      {isParticulier == "false" && (
                        <Paper className={classes.paper}>
                          <Society />
                        </Paper>
                      )}
                    </Grid>

                    <Grid
                      item
                      lg={12}
                      md={12}
                      xs={12}
                      className={classes.gridCell}
                    ></Grid>

                    <Grid item lg={12} md={12} xs={12}>
                      <Typography variant="h4" className={classes.titleSection}>
                        Loyer
                      </Typography>
                    </Grid>

                    <Grid item lg={12} md={12} xs={12}>
                      <Paper className={classes.paper}>
                        <Grid container spacing={2}>
                          <Grid item lg={4} md={4} xs={4}>
                            <TextField
                              size="small"
                              label="Fixe"
                              variant="outlined"
                              fullWidth
                            />
                          </Grid>

                          <Grid item lg={4} md={4} xs={4}>
                            <TextField
                              size="small"
                              label="Charges"
                              variant="outlined"
                              fullWidth
                            />
                          </Grid>
                          {isCommercialLocal == "true" && (
                            <Grid item lg={4} md={4} xs={4}>
                              <TextField
                                size="small"
                                label="TVA en %"
                                variant="outlined"
                                fullWidth
                              />
                            </Grid>
                          )}

                          <Grid item xs={6}>
                            <Typography variant="overline">
                              {" "}
                              Indice Insee de ref :T1
                            </Typography>
                          </Grid>

                          <Grid
                            item
                            lg={6}
                            md={6}
                            xs={6}
                            className={classes.cellRight}
                          >
                            <Typography variant="h5"> Total : 3000 </Typography>
                          </Grid>
                        </Grid>
                      </Paper>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Grid>
          ))}
          </Grid>
      </Grid>
      </Grid>
    </div>
  );
};

export default Accommodation;
