import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import { Typography, Button } from "@material-ui/core";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin : theme.spacing(2)
  },
  content: {
    marginTop: theme.spacing(2),
  },
}));

const Receipt = () => {
  const classes = useStyles();
  const [data, setData] = useState(null);

  const createPDF = () => {
    console.log("create PDF")

        const input = document.getElementById('divToPrint');
        if (imput != null){
            html2canvas(input)
            .then((canvas) => {
              setData (canvas.toDataURL('image/png'));
            });
        }


  }

  function downloadPDF() {
      if (data != null){


      console.log("download PDF")
      const pdf = new jsPDF();
            pdf.addImage(data, 'JPEG', 0, 0);
            // pdf.output('dataurlnewwindow');
            pdf.save("download.pdf");
      }
  }


  return (
    <div className={classes.root}>
        <h1>HELLO ON VA FAIRE DES QUITTANCES</h1>
        <Button className={classes.root} variant="contained" onClick={createPDF()}>CREATE PDF</Button>
        <Button className={classes.root}variant="contained" onClick={downloadPDF()}>Download PDF</Button>
        <div id="divToPrint">
            <div>Note: Here the dimensions of div are same as A4</div> 
            <div>You Can add any component here</div>
        </div>
    </div>
  );
};

export default Receipt;
