import React, { useState, useEffect } from "react";
import { makeStyles, withStyles } from "@material-ui/styles";
import { func } from "prop-types";
import { Typography, Grid, TextField, Button } from "@material-ui/core";
import {
  getIndexesAPI,
  updateIndexesAPI,
  saveNewIndex,
} from "request/settingsAPI";

const useStyles = makeStyles((theme) => ({
  root: {},
  button: {
    marginBottom: 30,
  },
  titleCell: {
    marginBottom: 10,
  },
  cellRight: {
    textAlign: "right",
    paddingRight: 25,
  },
}));

const Templates = () => {
  const classes = useStyles();
  const [listTemplate, setTemplates] = useState(["Template 1", "Template 2"]);
  const [showTemplate, setShowTemplate]=useState(true)

  function getIndexes() {
    getIndexesAPI((response) => {});
  }

  useEffect(() => {
    getIndexes();
  }, []);

  return (
    <div className={classes.root}>
      <TemplateShow
        open={showTemplate}
        onClose={(pedal) => {
            setShowTemplate(false);
        }}
      />
      <Grid container spacing={5}>
        <Grid item lg={6} md={6} xs={6} className={classes.title}>
          <Typography variant="h1">Les templates</Typography>
          <Typography variant="body1">
            Ici tu peux modifier les locataires des logements ainsi qu'ajouter
            un logement via le button ajouter.
          </Typography>
        </Grid>
        <Grid item lg={6} md={6} xs={6} className={classes.cellRight}>
          <Button variant="contained">Ajouter un template</Button>
        </Grid>
        <Grid item lg={12} md={12} xs={12}>
          <Button variant="contained">Voir</Button>
          <Button variant="contained">Modifier</Button>
        </Grid>

        {listTemplate.map((year) => (
          <>
            <Grid item lg={4} md={4} xs={4} className={classes.title}>
              <Typography
                variant="h5"
                className={classes.titleCell}
              ></Typography>
              <Grid container spacing={2}></Grid>
            </Grid>
          </>
        ))}
      </Grid>
    </div>
  );
};

export default Templates;
