import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  IconButton,
  CardHeader,
  TextField,
  Divider,
  Radio,
  RadioGroup,
  FormControlLabel,
} from "@material-ui/core";

import DeleteOutlineRounded from "@material-ui/icons/DeleteOutlineRounded";
import CreateRounded from "@material-ui/icons/CreateRounded";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData("Frozen yoghurt", 159, 6.0, 24, 4.0),
  createData("Ice cream sandwich", 237, 9.0, 37, 4.3),
  createData("Eclair", 262, 16.0, 24, 6.0),
  createData("Cupcake", 305, 3.7, 67, 4.3),
  createData("Gingerbread", 356, 16.0, 49, 3.9),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  loyerBox: {
    marginLeft: 12,
  },
  actionBar: {
    backgroundColor: "#A6A6A6",
  },
  titleBar: {
    backgroundColor: "#24A6A6"
  },
  gridCell: {
    alignItems: "center",
  },
  divider: {
    margin: theme.spacing(2, 0),
    backgroundColor: "#FFF000",
  },
}));

const Accommodation = () => {
  const classes = useStyles();
  const [isCommercialLocal, setCommercialLocal] = React.useState("true");

  const handleChange = (event) => {
    setCommercialLocal(event.target.value);
  };

  return (
    <div className={classes.root}>
      <Typography variant="h1">VOICI TES LOGEMENTS</Typography>

      <Grid container spacing={5}>
        {rows !== null &&
          rows.map((product) => (
            <Grid item key={product.name} lg={6} md={6} xs={12}>
              <Card variant="contained">
                <CardHeader
                  className={classes.titleBar}
                  title="23 RUE HENRI MARTIN - 92170 VANVES"
                  subheader="Appartement 1A"
                  action={
                    <div>
                      <IconButton aria-label="settings">
                        <DeleteOutlineRounded color="action" />
                      </IconButton>
                      <IconButton aria-label="settings">
                        <CreateRounded />
                      </IconButton>
                    </div>
                  }
                />
                <CardContent>
                  <Grid container spacing={1}>
                    <Grid
                      item
                      lg={12}
                      md={12}
                      xs={12}
                      className={classes.gridCell}
                    >
                      <RadioGroup
                        row
                        aria-label="position"
                        name="position"
                        defaultValue="top"
                        onChange={handleChange}
                      >
                        <FormControlLabel
                          value={"true"}
                          control={
                            <Radio
                              color="primary"
                              checked={isCommercialLocal === "true"}
                            />
                          }
                          label="local Commercial"
                          labelPlacement="right"
                        />
                        <FormControlLabel
                          value={"false"}
                          control={
                            <Radio
                              color="primary"
                              checked={isCommercialLocal === "false"}
                            />
                          }
                          label="Habitation"
                          labelPlacement="right"
                        />
                      </RadioGroup>
                    </Grid>

                    <Grid
                      item
                      lg={6}
                      md={6}
                      xs={6}
                      className={classes.gridCell}
                    >
                      <Typography variant="h6">Locataire(s):</Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <RadioGroup
                        row
                        aria-label="position"
                        name="position"
                        defaultValue="top"
                        onChange={handleChange}
                      >
                        <FormControlLabel
                          value={"true"}
                          control={
                            <Radio
                              color="primary"
                              checked={isCommercialLocal === "true"}
                            />
                          }
                          label="Particulier"
                          labelPlacement="right"
                        />
                        <FormControlLabel
                          value={"false"}
                          control={
                            <Radio
                              color="primary"
                              checked={isCommercialLocal === "false"}
                            />
                          }
                          label="Entreprise"
                          labelPlacement="right"
                        />
                      </RadioGroup>
                    </Grid>

                    <Divider className={classes.divider} />

                    <Grid item lg={12} md={12} xs={12}>
                      <Typography variant="h6">Loyer</Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <Typography className={classes.loyerBox}>
                        Fixe:
                      </Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <TextField
                        size="small"
                        label="Outlined"
                        variant="outlined"
                      />
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <Typography className={classes.loyerBox}>
                        Charge:
                      </Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <TextField
                        size="small"
                        label="Outlined"
                        variant="outlined"
                      />
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Grid>
          ))}
      </Grid>
    </div>
  );
};

export default Accommodation;
