import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { makeStyles, withStyles } from "@material-ui/styles";
import {
  Modal,
  Card,
  CardHeader,
  CardContent,
  CardActions,
  Grid,
  Divider,
  Typography,
  Radio,
  RadioGroup,
  FormControlLabel,
  MenuItem,
  Button,
  TextField,
  Select,
  InputLabel,
} from "@material-ui/core";
import firebase from "firebase/app";

const useStyles = makeStyles((theme) => ({
  root: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    outline: "none",
    boxShadow: theme.shadows[20],
    width: "60%",
    maxHeight: "90%",
    overflowY: "auto",
    maxWidth: "100%",
  },
  actions: {
    justifyContent: "flex-end",
  },
  center: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  divider: {
    margin: 10,
  },
  titleSection: {
    marginBottom: 2,
    marginRight: 15,
    marginLeft: 10,
  },
  gridCell: {
    alignItems: "center",
  },
  textField: {
    marginTop: theme.spacing(2),
  },
}));

function AddEstateModal({ open, className, onClose, ...rest }) {
  const classes = useStyles();
  var database = firebase.database();
  const [currentOwner, setCurrentOwner] = useState({
    isValid: false,
    values: {},
    touched: {},
    errors: {},
  });

  function createProprioParticulier() {
    const timestamp = Date.now();

    database.ref("id_user/owners/" + timestamp).set({
      id: timestamp,
      civility: currentOwner.values.civility,
      lastname: currentOwner.values.lastname,
      firstname: currentOwner.values.firstname,
      isSociety: false,
      socialIdentity: null,

      address: {
        postalCode: "92789",
        city: "Marseille",
        street: "23 rue henri martin",
      },
      isOwner: true,
    });

    onClose();
  }

  function createProprioEntreprise() {
    const timestamp = Date.now();

    database.ref("id_user/owners/" + timestamp).set({
      id: timestamp,
      civility: null,
      lastname: null,
      firstname: null,
      isSociety: true,
      socialIdentity: currentOwner.values.socialIdentity,
      address: {
        postalCode: currentOwner.values.postalCode,
        city: currentOwner.values.city,
        street: currentOwner.values.street,
      },
      isOwner: true,
    });

    onClose();
  }

  function createProprio() {
    if (currentOwner.values.isParticulier === "true") {
      createProprioParticulier();
    } else {
      createProprioEntreprise();
    }
  }

  const handleChange = (event) => {
    event.persist();

    setCurrentOwner((currentOwner) => ({
      ...currentOwner,
      values: {
        ...currentOwner.values,
        [event.target.name]:
          event.target.type === "checkbox"
            ? event.target.checked
            : event.target.value,
      },
      touched: {
        ...currentOwner.touched,
        [event.target.name]: true,
      },
    }));
  };

  const cancelClose = () => {
    onClose();
  };

  const hasError = (field) =>
    currentOwner.touched[field] && currentOwner.errors[field] ? true : false;

  if (!open) {
    return null;
  }

  return (
    <Modal onClose={onClose} open={open}>
      <Card {...rest} className={clsx(classes.root, className)}>
        <CardHeader title={"Ajouter un nouveau bien"} />
        <Divider />
        <CardContent>
          <RadioGroup
            row
            aria-label="isCommercial"
            name="isCommercial"
            defaultValue="top"
            onChange={handleChange}
            className={classes.gridCell}
          >
            <Typography variant="h4" className={classes.titleSection}>
              1.
            </Typography>
            <FormControlLabel
              value={"true"}
              control={
                <Radio
                  color="primary"
                  checked={currentOwner.values.isCommercial === "false"}
                />
              }
              label="Habitation"
              labelPlacement="right"
            />
            <FormControlLabel
              value={"false"}
              control={
                <Radio
                  color="primary"
                  checked={currentOwner.values.isCommercial === "true"}
                />
              }
              label="Local Commercial"
              labelPlacement="right"
            />
          </RadioGroup>

          <Typography variant="h4" className={classes.titleSection}>
            2. Adresse
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                size="small"
                error={hasError("street")}
                fullWidth
                helperText={
                  hasError("street") ? currentOwner.errors.street[0] : null
                }
                label="Numero et rue"
                name="street"
                onChange={handleChange}
                type="text"
                value={currentOwner.values.street || ""}
                variant="outlined"
              />
            </Grid>
            <Grid item xs={2}>
              <TextField
                size="small"
                error={hasError("postalCode")}
                fullWidth
                helperText={
                  hasError("postalCode")
                    ? currentOwner.errors.postalCode[0]
                    : null
                }
                label="Code Postal"
                name="postalCode"
                onChange={handleChange}
                type="text"
                value={currentOwner.values.postalCode || ""}
                variant="outlined"
              />
            </Grid>
            <Grid item xs={9}>
              <TextField
                size="small"
                error={hasError("city")}
                fullWidth
                helperText={
                  hasError("city") ? currentOwner.errors.city[0] : null
                }
                label="City"
                name="city"
                onChange={handleChange}
                type="text"
                value={currentOwner.values.city || ""}
                variant="outlined"
              />
            </Grid>
          </Grid>
        </CardContent>
        <Divider />
        <CardActions disableSpacing>
          <Button onClick={cancelClose}>annuler</Button>
          <Button onClick={createProprio}>créer</Button>
        </CardActions>
      </Card>
    </Modal>
  );
}

AddEstateModal.propTypes = {
  className: PropTypes.string,
  onClose: PropTypes.func,
  open: PropTypes.bool,
};

AddEstateModal.defaultProps = {
  open: false,
  onClose: () => {},
};

export default AddEstateModal;
