import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { makeStyles } from "@material-ui/styles";
import {
  Card,
  CardContent,
  CardActions,
  Typography,
  Grid,
  TextField,
  Paper,
  Radio,
  RadioGroup,
  FormControlLabel,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
  imageContainer: {
    marginRight: 20,
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: "5px",
    overflow: "hidden",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    justifyItems: "center",
  },
  image: {
    width: "100%",
  },
  statsItem: {
    display: "flex",
    alignItems: "center",
  },
  statsIcon: {
    color: theme.palette.icon,
    marginRight: theme.spacing(1),
  },
}));

const Particulier = ( {  handleChange, currentOwner, ...rest }) => {
  const [currentOwnerLocal, setCivility] = useState({
    isValid: false,
    values: {},
    touched: {},
    errors: {},
  });


  useEffect((currentOwner) => {

    console.log("current user", currentOwner)
    setCivility(currentOwner);
  }, []);

  return (
    <Grid container spacing={2}>
      <Grid item xs={6}>
        <RadioGroup
          row
          name="civility"
          defaultValue="top"
          onChange={handleChange}
        >
          <FormControlLabel
            value={"mr"}
            control={<Radio color="primary" checked={currentOwnerLocal.values.civility === "mr"} />}
            label="Mr"
            labelPlacement="right"
          />
          <FormControlLabel
            value={"mme"}
            control={<Radio color="primary" checked={currentOwnerLocal.values.civility === "mme"} />}
            label="Mme"
            labelPlacement="right"
          />
        </RadioGroup>
      </Grid>

      <Grid item xs={6} style={{ textAlign: "right" }}>
        <Typography variant="overline"> Date d'entrée : 12/01/2020</Typography>
      </Grid>
      <Grid item xs={6}>
        <TextField
          size="small"
          fullWidth
          label="Nom"
          name="lastname"
          onChange={handleChange}
          type="text"
          value={currentOwnerLocal.values.lastname || ""}
          variant="outlined"
        />
      </Grid>
      <Grid item xs={6}>
        <TextField
          size="small"
          fullWidth
          label="Prénom"
          name="firstname"
          onChange={handleChange}
          type="text"
          value={currentOwnerLocal.values.firstname || ""}
          variant="outlined"
        />
      </Grid>
    </Grid>
  );
};

Particulier.propTypes = {
  className: PropTypes.string,
  product: PropTypes.object.isRequired,
};

export default Particulier;
