import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import { Typography, Button, Divider, IconButton, Card } from "@material-ui/core";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import html2canvas from "html2canvas";
import Template from "./Template";
import jsPDF from "jspdf";
import { AddRounded } from "@material-ui/icons";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData(
    "23 rue henri Martin - 92170",
    "Appartement 1A",
    "M. CETRE",
    24,
    4.0
  ),
  createData("23 rue henri Martin - 92170", "Maison", "M. Fitoussi", 24, 4.0),
  createData(
    "23 rue henri Martin - 92170",
    "Appartement 3A",
    "M. BAUMERT",
    24,
    4.0
  ),
  createData(
    "67 rue des champignons- 92170",
    "Appartement RDC",
    "M. MACRON",
    24,
    4.0
  ),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin: theme.spacing(2),
  },
  content: {
    marginBottom: theme.spacing(2),
  },
  destinataire: {
    color: theme.palette.primary.main,
  },
  expediteur: {
    textAlign: "right",
  },
  page: {
    position: "absolute",
    opacity: 0,
    pointerEvents: "none",
  },
  pageBis: {
    backgroundColor: "#fff000",
    width: "210mm",
    height: "297mm",
    padding: 30,
  },
  divider: {
    margin: 20,
  },
}));

const ChooseBoos = () => {
  const classes = useStyles();
  const currentDate = new Date().toLocaleString("default", {
    month: "long",
    year: "numeric",
  });

  return (
    <div className={classes.root}>
      <div className={classes.content}>
        <Typography variant="h2">EDITION DES QUITTANCES ET APPELS DE LOYER</Typography>
        <Typography variant="overline">
          Date actuelle:{" "}
          {currentDate.toLocaleString("default", { month: "long" })}
        </Typography>
      </div>


            {rows.map((row) => (
              <Card variant="contained">
              <CardHeader
                className={classes.titleBar}
                title={
                  <Typography variant="h3" className={classes.textWhite}>
                    {" "}
                    23 RUE HENRI MARTIN - 92170 VANVES
                  </Typography>
                }
                subheader={
                  <Typography variant="h4" className={classes.textWhite}>
                    {" "}
                    Appartement 1A
                  </Typography>
                }
                action={
                  <div>
                    <IconButton aria-label="settings">
                      <DeleteRounded
                        fontSize="large"
                        style={{ color: colors.grey[50] }}
                      />
                    </IconButton>
                    <IconButton aria-label="settings">
                      <CreateRounded
                        fontSize="large"
                        style={{ color: colors.grey[50] }}
                      />
                    </IconButton>
                  </div>
                }
              />
              <CardContent>
                  </CardContent>
                  </Card>
            ))}
          </div>
  );
};

export default ChooseBoos;
