import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import { Typography, Button, Divider } from "@material-ui/core";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import html2canvas from "html2canvas";
import Template from "./Template"
import jsPDF from "jspdf";

function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
  }

const rows = [
    createData('23 rue henri Martin - 92170', "Appartement 1A", "M. CETRE", 24, 4.0),
    createData('23 rue henri Martin - 92170', "Maison", "M. Fitoussi", 24, 4.0),
    createData('23 rue henri Martin - 92170', "Appartement 3A", "M. BAUMERT", 24, 4.0),
    createData('67 rue des champignons- 92170', "Appartement RDC", "M. MACRON", 24, 4.0)
  ];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin : theme.spacing(2)
  },
  content: {
    marginTop: theme.spacing(2),
  },
  destinataire:{
      color: theme.palette.primary.main
  },
  expediteur:{
    textAlign: "right" 
}, page:{
        position: "absolute",
        opacity: 0,
        pointerEvents:"none"
},
pageBis:{
    backgroundColor: '#fff000',
        width: '210mm',
        height: '297mm',
        padding: 30
},
divider:{
    margin: 20
},
test:{
    display : "row"
}
}));

const Receipt = () => {
  const classes = useStyles();
  const currentDate = new Date().toLocaleString('default', { month: 'long' , year : 'numeric'});
  const text = "<div> <div>HELLO </div> <div style={text-align:'right'}> WORLD </div></div>"

  function createPDF() {
    console.log("create PDF")

        const input = document.querySelector('.divToPrint');
        if (input != null){
            html2canvas(input)
            .then((canvas) => {
                const image = canvas.toDataURL('image/png');
                const pdf = jsPDF("p", "mm", "a4");
                const width = 210;
                const height = 297;
            pdf.addImage(image,0,0,width,height);
            pdf.save("download.pdf");

            });
        } else {
            console.log("isNULL")
        }
  }


  return (
    <div className={classes.root}>
        <h1>HELLO ON VA FAIRE DES QUITTANCES</h1>
        <h2>{currentDate.toLocaleString('default', { month: 'long' })}</h2>

        <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Logements</TableCell>
            <TableCell align="right">Locataire</TableCell>
            <TableCell align="right">Montant du loyer</TableCell>
            <TableCell align="right">Appel de loyer</TableCell>
            <TableCell align="right">Quittance</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.name}>
              <TableCell component="th" scope="row">
                  <div className={classes.test}>
                {row.name} {row.calories}
                </div>
              </TableCell>
              <TableCell align="right">{row.fat}</TableCell>
              <TableCell align="right">{row.carbs}</TableCell>
              <TableCell align="right"><Button size="small" variant="contained" onClick={createPDF}>{currentDate}</Button></TableCell>
              <TableCell align="right"><Button size="small" variant="contained" onClick={createPDF}>{currentDate}</Button></TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>


        <div id="divToPrint" className={classes.page} >
            <div class="divToPrint ">
                <div className={classes.pageBis} >
            <div className={classes.destinataire}>
                <p>Monsieur Macron</p>
                <p>14 rue de Massenet</p>
                <p>75016 Paris</p>
            </div> 
            <div className={classes.expediteur}>
                <p>Locataire</p>
                <p>Adresse</p>
                <p>Ville</p>
            </div>

            <div>
                <h1>QUITTANCE DE LOYER</h1>
                <Divider className={classes.divider}/>
            </div>
            <div>
                <p>Mademoiselle,</p>
                <Divider className={classes.divider}/>
                <p>Je vous remercie d'avoir effectué le versement du montant de votre loyer pour la periode du x au x MONTH pour les locaaux que vous occupez sis à VILLES, ADRESSE, et dont le detail est cidessous:</p>
            </div>
            </div>
            </div>
        </div>
    </div>
  );
};

export default Receipt;
