import React, { useState, useEffect } from "react";
import clsx from "clsx";
import { makeStyles, withStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Modal,
  Card,
  CardContent,
  CardActions,
  Button,
  CardHeader,
} from "@material-ui/core";
import {
  getIndexesAPI,
  updateIndexesAPI,
  saveNewIndex,
} from "request/settingsAPI";
import TemplateGenerator from "./TemplateGenerator";

const useStyles = makeStyles((theme) => ({
  root: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    outline: "none",
    boxShadow: theme.shadows[20],
    width: "60%",
    maxHeight: "90%",
    overflowY: "auto",
    maxWidth: "100%",
  },
  button: {
    marginBottom: 30,
  },
  titleCell: {
    marginBottom: 10,
  },
  cellRight: {
    textAlign: "right",
    paddingRight: 25,
  },
}));

const fakeAccomodation = {
  name: "Template quittance",
  id: 768,
  textBody: "HELLO LES copains",
  textStart: "coucou",
  textEnd: "HHHHH",
};

const Templates = ({ open, className, onClose, ...rest }) => {
  const classes = useStyles();
  const [template, setTemplate] = useState({
    name: "Template quittance",
    id: 768,
    textBody: "HELLO LES copains",
    textStart: "coucou",
    textEnd: "HHHHH",
  });

  const cancelClose = () => {
    onClose();
  };

  if (!open) {
    return null;
  }

  return (
    <Modal onClose={onClose} open={open}>
      <Card {...rest} className={clsx(classes.root, className)}>
        <CardHeader title={template.name} />
        <CardContent>
          <TemplateGenerator accomodation={fakeAccomodation}/>
        </CardContent>
        <CardActions disableSpacing>
          <Button onClick={cancelClose}>annuler</Button>
          <Button>Modifier</Button>
        </CardActions>
      </Card>
    </Modal>
  );
};

export default Templates;
