import React, { useState, useEffect } from "react";
import clsx from "clsx";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/styles";
import {
  Card,
  CardHeader,
  CardContent,
  CardActions,
  Divider,
  Grid,
  Button,
  TextField,
  Typography,
} from "@material-ui/core";
import { useOwner } from "../../../../context/owner";

const useStyles = makeStyles(() => ({
  root: {},
}));

const AccountDetails = (props) => {
  const { className, ...rest } = props;
  const classes = useStyles();
  const { ownerInformations } = useOwner();
  const [localOwner, setLocalOwner] = useState({
    firstname: "",
    lastname: "",
    address: "",
  });

  const handleChange = (event) => {
    setLocalOwner({
      ...localOwner,
      [event.target.name]: event.target.value,
    });
  };

  useEffect(() => {
    if (ownerInformations !== undefined) {
      console.log("owner", ownerInformations);
      setLocalOwner(ownerInformations);
    }
  }, [ownerInformations]);

  return (
    <Card {...rest} className={clsx(classes.root, className)}>
      <form autoComplete="off" noValidate>
        <CardHeader title="Profile" />
        <Divider />
        <CardContent>
          <Grid container spacing={1}>
            <Grid item md={6} xs={12}>
              <TextField
                fullWidth
                helperText="Please specify the first name"
                label="First name"
                margin="dense"
                name="firstName"
                onChange={handleChange}
                required
                value={localOwner.firstname || ""}
                variant="outlined"
              />
            </Grid>
            <Grid item md={6} xs={12}>
              <TextField
                fullWidth
                label="Last name"
                margin="dense"
                name="lastName"
                onChange={handleChange}
                required
                value={localOwner.lastname || ""}
                variant="outlined"
              />
            </Grid>
            <Grid item md={12} xs={12}>
              <Typography>Address</Typography>
            </Grid>

            <Grid item md={12} xs={12}>
              <TextField
                fullWidth
                label="Address"
                margin="dense"
                name="email"
                onChange={handleChange}
                required
                variant="outlined"
              />
            </Grid>
            <Grid item md={3} xs={6}>
              <TextField
                fullWidth
                label="Address"
                margin="dense"
                name="email"
                onChange={handleChange}
                required
                variant="outlined"
              />
            </Grid>
            <Grid item md={9} xs={6}>
              <TextField
                fullWidth
                label="Address"
                margin="dense"
                name="email"
                onChange={handleChange}
                required
                variant="outlined"
              />
            </Grid>
          </Grid>
        </CardContent>
        <Divider />
        <CardActions>
          <Button color="primary" variant="contained">
            Save details
          </Button>
        </CardActions>
      </form>
    </Card>
  );
};

AccountDetails.propTypes = {
  className: PropTypes.string,
};

export default AccountDetails;
