import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { makeStyles } from "@material-ui/styles";
import {
  Card,
  CardContent,
  CardActions,
  Typography,
  Grid,
  TextField,
  Paper,
  Radio,
  RadioGroup,
  FormControlLabel,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
  imageContainer: {
    marginRight: 20,
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: "5px",
    overflow: "hidden",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    justifyItems: "center",
  },
  image: {
    width: "100%",
  },
  statsItem: {
    display: "flex",
    alignItems: "center",
  },
  statsIcon: {
    color: theme.palette.icon,
    marginRight: theme.spacing(1),
  },
}));

const Particulier = (props) => {
  const {  handleChange, currentOwner, ...rest } = props;
  const classes = useStyles();
  const [civility, setCivility] = React.useState("mr");

  const handleChangeCivility = (event) => {
    setCivility(event.target.value);
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={6}>
        <RadioGroup
          row
          aria-label="position"
          name="position"
          defaultValue="top"
          onChange={handleChange}
        >
          <FormControlLabel
            value={"mr"}
            control={<Radio color="primary" checked={currentOwner.values.civility === "mr"} />}
            label="Mr"
            labelPlacement="right"
          />
          <FormControlLabel
            value={"mme"}
            control={<Radio color="primary" checked={currentOwner.values.civility === "mme"} />}
            label="Mme"
            labelPlacement="right"
          />
        </RadioGroup>
      </Grid>

      <Grid item xs={6} style={{ textAlign: "right" }}>
        <Typography variant="overline"> Date d'entrée : 12/01/2020</Typography>
      </Grid>
      <Grid item xs={6}>
        <TextField
          size="small"

          fullWidth


          label="Nom"
          name="lastname"
          onChange={handleChange}
          type="text"
          value={currentOwner.values.lastname || ""}
          variant="outlined"
        />
      </Grid>
      <Grid item xs={6}>
        <TextField
          size="small"
          fullWidth

          label="Prénom"
          name="firstname"
          onChange={handleChange}
          type="text"
          value={currentOwner.values.firstname || ""}
          variant="outlined"
        />
      </Grid>
    </Grid>
  );
};

Particulier.propTypes = {
  className: PropTypes.string,
  product: PropTypes.object.isRequired,
};

export default Particulier;
