  Divider,
  IconButton,
  Card,
  CardHeader,
  Paper,
  Grid,
  Avatar,
} from "@material-ui/core";
import { colors } from "@material-ui/core";
import firebase from "firebase/app";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData(
    "23 rue henri Martin - 92170",
    "Appartement 1A",
    "M. CETRE",
    24,
    4.0
  ),
  createData("23 rue henri Martin - 92170", "Maison", "M. Fitoussi", 24, 4.0),
  createData(
    "23 rue henri Martin - 92170",
    "Appartement 3A",
    "M. BAUMERT",
    24,
    4.0
  ),
  createData(
    "67 rue des champignons- 92170",
    "Appartement RDC",
    "M. MACRON",
    24,
    4.0
  ),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin: theme.spacing(2)
  },
  content: {
    marginBottom: theme.spacing(2),
  },
  destinataire: {
    color: theme.palette.primary.main,
  },
  expediteur: {
    textAlign: "right",
  },
  centerText: {
    textAlign: "center",
  },imageContainer: {
    marginRight: 20,
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: '5px',
    overflow: 'hidden',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    justifyItems: 'center'
  },
  image: {
    width: '50%'
  },
  pageBis: {
    backgroundColor: "#fff000",
    width: "210mm",
    height: "297mm",
    padding: 30,
  },
  paper: {
    padding: 20,
    textAlign: "center",
    cursor: "pointer"
  },
}));

const ChooseBoos = () => {
  const classes = useStyles();
  var database = firebase.database();

  function getBossInformations() {
