import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Button,
  Divider,
  IconButton,
  Card,
  CardHeader,
  Paper,
  Grid,
  Avatar,
} from "@material-ui/core";
import { colors } from "@material-ui/core";
import firebase from "firebase/app";
import AddOwnerModal from "./components/AddOwnerModal";
import { getOwnerDataBase } from "request/ownerAPI";
import { useUser } from "../../context/userInformations";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin: theme.spacing(2),
  },
  content: {
    marginBottom: theme.spacing(2),
  },
  destinataire: {
    color: theme.palette.primary.main,
  },
  expediteur: {
    textAlign: "right",
  },
  centerText: {
    textAlign: "center",
  },
  imageContainer: {
    marginRight: 20,
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: "5px",
    overflow: "hidden",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    justifyItems: "center",
  },
  image: {
    width: "50%",
  },
  paper: {
    padding: 20,
    textAlign: "center",
    cursor: "pointer",
    backgroundColor: theme.palette.primary.main,
  },
}));

const ChooseBoos = (props) => {
  const { history } = props;
  const classes = useStyles();
  const { setUserInformations } = useUser();
  const [showAddOwner, setShowAddOwner] = useState(false);
  const [owners, setData] = useState(null);

  function getBossInformations() {
    getOwnerDataBase((response) => {
      console.log("response get owner", response);
      setData(response);
    });
  }

  function handleAddOwner() {
    setShowAddOwner(true);
  }

  function handleClickCard(owner) {
    console.log(owner);
    setUserInformations(owner);
    history.push("/accommodations");
  }

  useEffect(() => {
    getBossInformations();
  }, []);

  console.log("owner", owners);

  return (
    <div className={classes.root}>
      <div className={classes.centerText}>LOGO</div>
      <Grid container>
        <Grid item xs={8}>
          <div className={classes.content}>
            <Typography variant="h2">Selectionne ton proprio !</Typography>
            <Typography variant="overline">
              Choisit le propriétaire dont tu souhaites voir les biens
            </Typography>
          </div>
        </Grid>
        <Grid item xs={4} className={classes.expediteur}>
          <Button
            variant="contained"
            onClick={handleAddOwner}
            className={classes.expediteur}
          >
            AJOUTER UN proprio
          </Button>
        </Grid>
      </Grid>

      <AddOwnerModal
        open={showAddOwner}
        onClose={(pedal) => {
          setShowAddOwner(false);
        }}
      />

      <Grid container spacing={4}>
        {owners != null &&
          Object.values(owners).map((owner, index) => (
            <Grid item xs={3}>
              <Paper
                elevation={0}
                variant="outlined"
                className={classes.paper}
                onClick={() => handleClickCard(owner)}
              >
                <img
                  alt="Product"
                  className={classes.image}
                  src={"/images/avatars/icon_" + index + ".svg"}
                />

                {!owner.isSociety && (
                  <Typography variant="h3">
                    {owner.firstname + " " + owner.lastname}
                  </Typography>
                )}
                {owner.isSociety && (
                  <Typography variant="h3">{owner.socialIdentity}</Typography>
                )}
              </Paper>
            </Grid>
          ))}
      </Grid>
    </div>
  );
};

export default ChooseBoos;
