import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { makeStyles, withStyles } from "@material-ui/styles";
import {
  Modal,
  Card,
  CardHeader,
  CardContent,
  CardActions,
  Grid,
  Divider,
  Typography,
  Radio,
  RadioGroup,
  FormControlLabel,
  MenuItem,
  Button,
  TextField,
  Select,
  InputLabel,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    outline: "none",
    boxShadow: theme.shadows[20],
    width: "60%",
    maxHeight: "90%",
    overflowY: "auto",
    maxWidth: "100%",
  },
  actions: {
    justifyContent: "flex-end",
  },
  center: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  divider: {
    margin: 10,
  },
  titleSection: {
    marginRight: 15,
    marginLeft: 10,
  },
  gridCell: {
    alignItems: "center",
  },
  textField: {
    marginTop: theme.spacing(2),
  },
  container: {
    marginTop: 0,
    marginBottom: theme.spacing(2),
  },
}));

const month = [
  "Jan",
  "Fev",
  "Mar",
  "Avr",
  "Mai",
  "Jun",
  "Jul",
  "Aou",
  "Sep",
  "Oct",
  "Nov",
  "Dec",
];

function MonthYearPicker({ open, handleChange, onClose, ...rest }) {
  const classes = useStyles();

  const cancelClose = () => {
    onClose();
  };

  if (!open) {
    return null;
  }

  return (
    <Modal onClose={onClose} open={open}>
      <Card>
        <CardHeader title={"Choisir un mois"} />
        <Divider />
        <CardContent>
          <Grid container>
            {month.map((row) => (
              <Grid item xs={3}>
                <Button>{row}</Button>
              </Grid>
            ))}
          </Grid>
        </CardContent>
      </Card>
    </Modal>
  );
}

MonthYearPicker.propTypes = {
  className: PropTypes.string,
  onClose: PropTypes.func,
  open: PropTypes.bool,
};

MonthYearPicker.defaultProps = {
  open: false,
  onClose: () => {},
};

export default MonthYearPicker;
