import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { makeStyles, withStyles } from "@material-ui/styles";
import {
  Modal,
  Card,
  CardHeader,
  CardContent,
  CardActions,
  Grid,
  Divider,
  Typography,
  Radio,
  RadioGroup,
  FormControlLabel,
  MenuItem,
  Button,
  IconButton,
  Select,
  InputLabel,
} from "@material-ui/core";
import { ArrowBackRounded, ArrowRightRounded } from "@material-ui/icons";

const useStyles = makeStyles((theme) => ({
  root: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    outline: "none",
    boxShadow: theme.shadows[20],
  },
}));

const month = [
  "Jan",
  "Fev",
  "Mar",
  "Avr",
  "Mai",
  "Jun",
  "Jul",
  "Aou",
  "Sep",
  "Oct",
  "Nov",
  "Dec",
];

function MonthYearPicker({ open, className, handleChange, onClose, ...rest }) {
  const classes = useStyles();
  const [year, setYear] = useState(2100)

  const cancelClose = () => {
    onClose();
  };

  if (!open) {
    return null;
  }

  return (
    <Modal onClose={onClose} open={open}>
      <Card {...rest} className={clsx(classes.root, className)}>
        <CardHeader
          title={year}
          action={
            <>
              <IconButton aria-label="settings">
                <ArrowBackRounded />
              </IconButton>
              <IconButton aria-label="settings">
                <ArrowRightRounded />
              </IconButton>
            </>
          }
        />
        <Divider />
        <CardContent>
          <Grid container spacing={3}>
            {month.map((row) => (
              <Grid item xs={3}>
                <Button variant="outlined" fullWidth>
                  {row}
                </Button>
              </Grid>
            ))}
          </Grid>
        </CardContent>
      </Card>
    </Modal>
  );
}

MonthYearPicker.propTypes = {
  className: PropTypes.string,
  onClose: PropTypes.func,
  open: PropTypes.bool,
};

MonthYearPicker.defaultProps = {
  open: false,
  onClose: () => {},
};

export default MonthYearPicker;
