import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";

import { Divider, Grid, Typography } from "@material-ui/core";

const lastDay = {
  janvier: 31,
  fevrier: 28,
  mars: 31,
  avril: 30,
  mai: 31,
  juin: 30,
  juillet: 31,
  aout: 31,
  septembre: 30,
  octobre: 31,
  novembre: 30,
  decembe: 31,
};

const civilityTranslation = {
  m: "Monsieur",
  mme: "Madame",
};

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin: theme.spacing(2),
  },
  content: {
    marginBottom: theme.spacing(2),
  },
  destinataire: {
    textAlign: "left",
  },
  expediteur: {
    textAlign: "right",
  },
  page: {
    position: "absolute",
    opacity: 0,
    pointerEvents: "none",
    fontSize: 30
  },
  pageBis: {
    backgroundColor: "#DCF3FF",
    width: "210mm",
    height: "297mm",
    padding: 30,
    fontSize: 20
  },
  divider: {
    margin: 10,
  },
  montant: {
    width: "50%",
    textAlign: "left",
  },
}));

const Template = ({ owner, accomodation, date, ...rest }) => {
  const classes = useStyles();

  function calculateTotal() {
    var numFixe = Number(accomodation.loyer.fixe);
    var numCharges = Number(accomodation.loyer.charges);
    var numTVA =
    accomodation.isCommercial == undefined ? Number(accomodation.loyer.tva) : 0;

    return numFixe + numCharges + numTVA;
  }

  return (
    <div id="divToPrint">
      <div class="divToPrint">
        <div className={classes.pageBis}>
          <div className={classes.destinataire}>
            <Typography variant="letter">
              {!owner.isSociety
                ? owner.firstname.toUpperCase() +
                  " " +
                  owner.lastname.toUpperCase()
                : owner.socialIdentity}
            </Typography>
            <Typography>{owner.address.street.toUpperCase()}</Typography>
            <Typography>
              {owner.address.postalCode.toUpperCase() +
                " " +
                owner.address.city.toUpperCase()}
            </Typography>
          </div>
          <div className={classes.expediteur}>
            {
              <Typography className={classes.expediteur}>
                {" "}
                {accomodation.rental.isParticulier
                  ? accomodation.rental.firstname.toUpperCase() +
                    " " +
                    accomodation.rental.lastname.toUpperCase()
                  : accomodation.rental.socialIdentity}
              </Typography>
            }
            <Typography>{accomodation.address.street.toUpperCase()}</Typography>
            <Typography>
              {accomodation.address.postalCode.toUpperCase() +
                " " +
                accomodation.address.city.toUpperCase()}
            </Typography>
          </div>

          <div className={classes.destinataire}>
            <Typography>
              {civilityTranslation[accomodation.rental.civility]},
            </Typography>
            <Divider className={classes.divider}/>
            <Typography>
              Je vous remercie d'avoir effectué le versement du montant de votre
              loyer pour la periode du 1er au {lastDay[date.month]} {date.month}{" "}
              pour les locaux que vous occupez sis à{" "}
              {accomodation.address.city.toUpperCase()},{" "}
              {accomodation.address.street.toUpperCase()}, et dont le detail est
              ci dessous:
            </Typography>
          </div>
          <Divider className={classes.divider}/>
          <div className={classes.montant}>
            <Grid container>
              <Grid item xs={6}>
                Loyer :
              </Grid>
              <Grid item xs={6} className={classes.expediteur}>
                {accomodation.loyer.fixe},00
              </Grid>
              <Grid item xs={6}>
                Provision sur charges :
              </Grid>
              <Grid item xs={6} className={classes.expediteur}>
                {accomodation.loyer.charges},00
              </Grid>

              {!accomodation.isCommercial && (
                <>
                  <Grid item xs={6}>
                    TVA :
                  </Grid>
                  <Grid item xs={6} className={classes.expediteur}>
                    {accomodation.loyer.tva},00
                  </Grid>
                </>
              )}
              <Grid item xs={6} />
              <Grid item xs={6} className={classes.expediteur}>
                ------------
              </Grid>
              <Grid item xs={6}>
                Total :
              </Grid>
              <Grid item xs={6} className={classes.expediteur}>
              {calculateTotal()},00
              </Grid>
            </Grid>
          </div>
          <Divider className={classes.divider}/>
          <Typography className={classes.destinataire}>Veuillez agréer, {civilityTranslation[accomodation.rental.civility]}, l'expression de mes sentiments distingués </Typography>
        </div>
      </div>
    </div>
  );
};

export default Template;
