import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Button,
  Divider,
  IconButton,
  Card,
  CardHeader,
  Paper,
  Grid,
  Avatar,
} from "@material-ui/core";
import { colors } from "@material-ui/core";
import firebase from "firebase/app";
import AddOwnerModal from "./components/AddOwnerModal";
import { getOwnerDataBase } from "request/ownerAPI";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData(
    "23 rue henri Martin - 92170",
    "Appartement 1A",
    "M. CETRE",
    24,
    4.0
  ),
  createData("23 rue henri Martin - 92170", "Maison", "M. Fitoussi", 24, 4.0),
  createData(
    "23 rue henri Martin - 92170",
    "Appartement 3A",
    "M. BAUMERT",
    24,
    4.0
  ),
  createData(
    "67 rue des champignons- 92170",
    "Appartement RDC",
    "M. MACRON",
    24,
    4.0
  ),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin: theme.spacing(2),
  },
  content: {
    marginBottom: theme.spacing(2),
  },
  destinataire: {
    color: theme.palette.primary.main,
  },
  expediteur: {
    textAlign: "right",
  },
  centerText: {
    textAlign: "center",
  },
  imageContainer: {
    marginRight: 20,
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: "5px",
    overflow: "hidden",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    justifyItems: "center",
  },
  image: {
    width: "50%",
  },
  pageBis: {
    backgroundColor: "#fff000",
    width: "210mm",
    height: "297mm",
    padding: 30,
  },
  paper: {
    padding: 20,
    textAlign: "center",
    cursor: "pointer",
  },
}));

const ChooseBoos = () => {
  const classes = useStyles();
  var database = firebase.database();
  const [showAddOwner, setShowAddOwner] = useState(false);
  const [owners, setData] = useState(null);

  function getBossInformations() {
    getOwnerDataBase((response) => {
      console.log("response get owner" , response)
      setData(response);
    });
  }

  function handleAddOwner() {
    setShowAddOwner(true);
  }

  useEffect(() => {
    getBossInformations();
  }, []);

console.log("owner", owners);

  return (
    <div className={classes.root}>
      <div className={classes.expediteur}>
        <Button variant="contained" onClick={handleAddOwner}>
          AJOUTER UN proprio
        </Button>
      </div>
      <div className={classes.content}>
        <Typography variant="h2" className={classes.centerText}>
          CHOISI TON PROPRIO !
        </Typography>
      </div>

      <AddOwnerModal open={showAddOwner} onClose={pedal => {
          setShowAddOwner(false);
        }} />

      <Grid container spacing={4}>
        {owners != null && Object.keys(owners).map((row) => (
          <Grid item xs={3}>
            <Paper
              elevation={0}
              variant="outlined"
              className={classes.paper}
              onClick={getBossInformations}
            >
              <img
                alt="Product"
                className={classes.image}
                src="/images/avatars/banana.png"
              />

              <Typography variant="h3">{row.firstname}</Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default ChooseBoos;
