import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { makeStyles, withStyles } from "@material-ui/styles";
import {
  Card,
  CardActions,
  CardContent,
  Avatar,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  TableContainer,
  CardHeader,
  Grid,
  Button,
} from "@material-ui/core";
import EditRounded from "@material-ui/icons/EditRounded";
import SaveRounded from "@material-ui/icons/SaveRounded";

const useStyles = makeStyles((theme) => ({
  root: {},
  row: {
    height: "42px",
    display: "flex",
    alignItems: "center",
    marginTop: theme.spacing(1),
  },
  spacer: {
    flexGrow: 1,
  },
  importButton: {
    marginRight: theme.spacing(1),
  },
  exportButton: {
    marginRight: theme.spacing(1),
  },
  searchInput: {
    marginRight: theme.spacing(1),
  },
  container: {
    maxHeight: 740,
  },
  headerTitle: {
    paddingBottom: 0,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
}));

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.background.default,
    },
  },
}))(TableRow);

const TableStockComponent = (props) => {
  const { className, listTable, ...rest } = props;

  const classes = useStyles();
  const [componentInfo, setComponentInfo] = useState({});
  const [modify, setModify] = useState(false);
  const sizeGrid = 12 / (Object.keys(listTable).length - 1);

  function handleModify() {
    setModify(true);
  }

  function handleSave() {
    setModify(true);
  }

  useEffect(() => {
    console.log("component tableGE", listTable);
    if (listTable !== undefined) {
      const index = Object.keys(listTable).findIndex(
        (p) => p.attr1 == "typeAvailable"
      );
      Object.keys(listTable).slice(index);
      console.log("size list", Object.keys(listTable).length - 1);
      console.log("size", 12 / (Object.keys(listTable).length - 1));

      setComponentInfo(listTable);
    }
  }, [listTable]);

  return (
    <Grid container spacing={3}>
      {Object.keys(componentInfo).map((keyName, i) => (
        <Grid item xs={sizeGrid === 12 ? 6 : sizeGrid}>
          {keyName !== "typeAvailable" && (
            <Card {...rest} className={clsx(classes.root, className)}>
              {keyName !== "values" && (
                <CardHeader
                  title={keyName}
                  style={{ textAlign: "center" }}
                  action={
                    <>
                      {!modify && (
                        <IconButton
                          aria-label="settings"
                          onClick={handleModify}
                        >
                          <EditRounded />
                        </IconButton>
                      )}
                      {modify && (
                        <IconButton
                          aria-label="settings"
                          onClick={handleSave}
                        >
                          <SaveRounded />
                        </IconButton>
                      )}
                    </>
                  }
                />
              )}
              <CardContent className={classes.content}>
                <TableContainer className={classes.container}>
                  <Table stickyHeader size="small">
                    <TableHead>
                      <TableRow>
                        <TableCell>Valeur</TableCell>
                        <TableCell>Stock</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {Object.values(componentInfo[keyName]).map((user) => (
                        <StyledTableRow>
                          <StyledTableCell>{user.label}</StyledTableCell>
                          <StyledTableCell>{user.stock}</StyledTableCell>
                        </StyledTableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </CardContent>
            </Card>
          )}
        </Grid>
      ))}
    </Grid>
  );
};

TableStockComponent.propTypes = {
  className: PropTypes.string,
  listTable: PropTypes.object,
};

export default TableStockComponent;
