import React, { useState, useEffect, withStyles } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { makeStyles } from "@material-ui/styles";
import {
  Card,
  CardContent,
  CardActions,
  Typography,
  Grid,
  TextField,
  Paper,
  Radio,
  RadioGroup,
  FormControlLabel,
} from "@material-ui/core";

const CssTextField = withStyles({
    root: {
      "& label.Mui-focused": {
        color: "green",
      },
      "& .MuiInput-underline:after": {
        borderBottomColor: "green",
      },
      "& .MuiOutlinedInput-root": {
        "& fieldset": {
          borderColor: "red",
        },
        "&:hover fieldset": {
          borderColor: "yellow",
        },
        "&.Mui-focused fieldset": {
          borderColor: "green",
        },
        "&.Mui-disabled fieldset": {
          borderColor: "#90D8FF",
        },
      },
      "& label.Mui-disabled": {
        color: "#486C7F",
      },
    },
  })(TextField);


const useStyles = makeStyles((theme) => ({
  root: {
      padding: theme.spacing(2)
  },
  imageContainer: {
    marginRight: 20,
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: "5px",
    overflow: "hidden",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    justifyItems: "center",
  },
  image: {
    width: "100%",
  },
  statsItem: {
    display: "flex",
    alignItems: "center",
  },
  statsIcon: {
    color: theme.palette.icon,
    marginRight: theme.spacing(1),
  },
}));

const Loyer = (props) => {
  const { className, product, components, disabled, handleChange, currentName, ...rest } = props;
  const classes = useStyles();
  const [nameSociety, setNameSociety] = useState("");
  const [isModifying, setModify] =useState(false);


  useEffect(() => {
    if (disabled !== undefined) setModify(disabled);

  }, [disabled]);

  useEffect(() => {
    if (currentName !== undefined) setNameSociety(currentName);

  }, [currentName]);

  return (


    <Paper elevation={0} className={classes.paper}>
    <Grid container spacing={2}>
      <Grid item lg={3} md={3} xs={3}>
        <TextField
          size="small"
          label="Fixe"
          variant="outlined"
          fullWidth
          disabled={!isModifying}
        />
      </Grid>

      <Grid item lg={3} md={3} xs={3}>
        <TextField
          size="small"
          label="Charges"
          variant="outlined"
          fullWidth
          disabled={!isModifying}
        />
      </Grid>
      {isCommercialLocal == "true" && (
        <Grid item lg={3} md={3} xs={3}>
          <TextField
            size="small"
            label="TVA en %"
            variant="outlined"
            fullWidth
            disabled={!isModifying}
          />
        </Grid>
      )}

      <Grid item lg={3} md={3} xs={3} className={classes.gridCell}>
        <CssTextField
          size="small"
          label="Total"
          variant="outlined"
          fullWidth
          value={3000}
          disabled
          inputProps={{ "aria-label": "naked" }}
        />
      </Grid>
    </Grid>
  </Paper>

  );
};

Loyer.propTypes = {
  className: PropTypes.string,
  product: PropTypes.object.isRequired,
};

export default Loyer;
