import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  IconButton,
  CardHeader,
  TextField,
  Divider,
  Radio,
  RadioGroup,
  FormControlLabel,
  Paper,
} from "@material-ui/core";
import { grey, red } from "@material-ui/core/colors";
import DeleteOutlineRounded from "@material-ui/icons/DeleteOutlineRounded";
import CreateRounded from "@material-ui/icons/CreateRounded";
import Particulier from "./Components/Particulier";
import Society from "./Components/Society";

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData("Frozen yoghurt", 159, 6.0, 24, 4.0),
  createData("Ice cream sandwich", 237, 9.0, 37, 4.3),
  createData("Eclair", 262, 16.0, 24, 6.0),
  createData("Cupcake", 305, 3.7, 67, 4.3),
  createData("Gingerbread", 356, 16.0, 49, 3.9),
];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  paper: {
    padding: 10,
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  loyerBox: {
    marginLeft: 12,
  },
  actionBar: {
    backgroundColor: "#A6A6A6",
  },
  titleBar: {
    backgroundColor: "#24A6A6",
  },
  gridCell: {
    alignItems: "center",
  },
  titleSection: {
    marginBottom: 5,
    marginRight: 5,
  },
  divider: {
    margin: 5,
    height: 2,
    width: "100%",
  },
}));

const Accommodation = () => {
  const classes = useStyles();
  const [isCommercialLocal, setCommercialLocal] = React.useState("true");
  const [isParticulier, setParticulier] = React.useState("true");

  const handleChangeCommercialLocal = (event) => {
    setCommercialLocal(event.target.value);
  };

  const handleChangeParticulier = (event) => {
    setParticulier(event.target.value);
  };

  return (
    <div className={classes.root}>
      <Typography variant="h1">VOICI TES LOGEMENTS</Typography>

      <Grid container spacing={5}>
        {rows !== null &&
          rows.map((product) => (
            <Grid item key={product.name} lg={6} md={6} xs={12}>
              <Card variant="contained">
                <CardHeader
                  className={classes.titleBar}
                  title="23 RUE HENRI MARTIN - 92170 VANVES"
                  subheader="Appartement 1A"
                  action={
                    <div>
                      <IconButton aria-label="settings">
                        <DeleteOutlineRounded style={{ color: red[50] }} />
                      </IconButton>
                      <IconButton aria-label="settings">
                        <CreateRounded style={{ color: grey[50] }} />
                      </IconButton>
                    </div>
                  }
                />
                <CardContent>
                  <Grid container spacing={1}>
                    <Grid
                      item
                      lg={12}
                      md={12}
                      xs={12}
                      className={classes.gridCell}
                    >
                      <RadioGroup
                        row
                        aria-label="position"
                        name="position"
                        defaultValue="top"
                        onChange={handleChangeCommercialLocal}
                      >
                        <FormControlLabel
                          value={"true"}
                          control={
                            <Radio
                              color="primary"
                              checked={isCommercialLocal === "true"}
                            />
                          }
                          label="local Commercial"
                          labelPlacement="right"
                        />
                        <FormControlLabel
                          value={"false"}
                          control={
                            <Radio
                              color="primary"
                              checked={isCommercialLocal === "false"}
                            />
                          }
                          label="Habitation"
                          labelPlacement="right"
                        />
                      </RadioGroup>
                    </Grid>

                    <Grid
                      item
                      lg={12}
                      md={12}
                      xs={12}
                      className={classes.gridCell}
                    >
                      <RadioGroup
                        row
                        aria-label="position"
                        name="position"
                        defaultValue="top"
                        onChange={handleChangeParticulier}
                        className={classes.gridCell}
                      >
                        <Typography
                          variant="h4"
                          className={classes.titleSection}
                        >
                          Locataire(s):
                        </Typography>
                        <FormControlLabel
                          value={"true"}
                          control={
                            <Radio
                              color="primary"
                              checked={isParticulier === "true"}
                            />
                          }
                          label="Particulier"
                          labelPlacement="right"
                        />
                        <FormControlLabel
                          value={"false"}
                          control={
                            <Radio
                              color="primary"
                              checked={isParticulier === "false"}
                            />
                          }
                          label="Entreprise"
                          labelPlacement="right"
                        />
                      </RadioGroup>

                      {isParticulier == "true" && (
                        <Paper className={classes.paper}>
                          <Particulier />
                        </Paper>
                      )}

                      {isParticulier == "false" && (
                        <Paper className={classes.paper}>
                          <Society />
                        </Paper>
                      )}
                    </Grid>

                    <Grid
                      item
                      lg={12}
                      md={12}
                      xs={12}
                      className={classes.gridCell}
                    ></Grid>

                    <Grid item lg={12} md={12} xs={12}>
                      <Typography variant="h4">Loyer</Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <Typography className={classes.loyerBox}>
                        Fixe:
                      </Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <TextField
                        size="small"
                        label="Outlined"
                        variant="outlined"
                      />
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <Typography className={classes.loyerBox}>
                        Charge:
                      </Typography>
                    </Grid>

                    <Grid item lg={6} md={6} xs={6}>
                      <TextField
                        size="small"
                        label="Outlined"
                        variant="outlined"
                      />
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Grid>
          ))}
      </Grid>
    </div>
  );
};

export default Accommodation;
