import Firebase from "firebase";



export const saveNewComponentInDataBase = (owner, id) => {
  Firebase.database().ref("id_user/owners/" + id).set({
    id: id,
    civility: null,
    lastname: null,
    firstname: null,
    isSociety: true,
    socialIdentity: owner.socialIdentity,
    address: {
      postalCode: owner.postalCode,
      city: owner.city,
      street: owner.street,
    },
    isOwner: true,
  })
};

export const deleteValueInDataBase = (type, soustype, index) => {
  Firebase.database()
    .ref(`/stock/componentsStock/${type}/${soustype}/${index}`)
    .set(null);
  console.log("DATA SAVED");
};


export const updateStockAfterAddPedals = (path, value) => {
  Firebase.database()
    .ref(`/stock/componentsStock/${path}/stock`)
    .set(value);
  console.log("DATA SAVED");
};

export const getOwnerDataBase = (callback) => {
  const existingUser = JSON.parse(localStorage.getItem("logged_user"));
  console.log('user logger info', existingUser)
  let ref = Firebase.database().ref("id_user/owners");
  ref.on("value", (snapshot) => {
    const data = snapshot.val();
    callback(data);
  });
};
