import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { makeStyles } from "@material-ui/styles";
import {
  Card,
  CardContent,
  CardActions,
  Typography,
  Grid,
  TextField,
  Paper,
  Radio,
  RadioGroup,
  FormControlLabel,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
      padding: theme.spacing(2)
  },
  imageContainer: {
    marginRight: 20,
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: "5px",
    overflow: "hidden",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    justifyItems: "center",
  },
  image: {
    width: "100%",
  },
  statsItem: {
    display: "flex",
    alignItems: "center",
  },
  statsIcon: {
    color: theme.palette.icon,
    marginRight: theme.spacing(1),
  },
}));

const Particulier = (props) => {
  const { className, product, components, ...rest } = props;
  const classes = useStyles();
  const [civility, setCivility] = React.useState("mr");

  const handleChangeCivility = (event) => {
    setCivility(event.target.value);
  };

  return (
    <Paper className={classes.root}>
      <Grid container spacing={1}>
        <Grid xs={12}>
          <RadioGroup
            row
            aria-label="position"
            name="position"
            defaultValue="top"
            onChange={handleChangeCivility}
          >
            <FormControlLabel
              value={"mr"}
              control={<Radio color="primary" checked={civility === "mr"} />}
              label="Mr"
              labelPlacement="right"
            />
            <FormControlLabel
              value={"mme"}
              control={<Radio color="primary" checked={civility === "mme"} />}
              label="Mme"
              labelPlacement="right"
            />
          </RadioGroup>
        </Grid>
        <Grid xs={6}>
          <TextField size="small" label="Nom" variant="outlined" fullWidth/>
        </Grid>
        <Grid xs={6}>
          <TextField size="small" label="Prénom" variant="outlined" fullWidth />
        </Grid>
      </Grid>
    </Paper>
  );
};

Particulier.propTypes = {
  className: PropTypes.string,
  product: PropTypes.object.isRequired,
};

export default Particulier;
