import Firebase from "firebase";

export const saveNewAccommodation = (accomodation, id, ownerId) => {
  Firebase.database().ref("id_user/accomodations/" + id).set({
    id: id,
    loyer : accomodation.loyer, 
    rental :Â {
        civility: accomodation.civility || null,
        lastname: accomodation.lastname || null,
        firstname: accomodation.firstname || null,
        isSociety: accomodation.isSociety || null,
        socialIdentity: accomodation.socialIdentity || null,
    },
    address: {
      postalCode: accomodation.postalCode,
      city: accomodation.city,
      street: accomodation.street,
      otherInformations : accomodation.otherInformations || null
    },
    isCommercial: accomodation.isCommercial,
    owner : ownerId
  })
};

export const deleteValueInDataBase = (type, soustype, index) => {
  Firebase.database()
    .ref(`/stock/componentsStock/${type}/${soustype}/${index}`)
    .set(null);
  console.log("DATA SAVED");
};


export const updateAccomodation = (value) => {
  Firebase.database()
    .ref(`/id_user/accomodations/${value.id}`)
    .set(value);
  console.log("DATA SAVED");
};

export const getAccomodationByOwner = (ownerID, callback) => {
  let ref = Firebase.database().ref("id_user/accomodations");
  ref.on("value", (snapshot) => {
    const data = snapshot.val();
    callback(data);
  });
};
