import React, { useState, useEffect } from "react";
import { makeStyles, withStyles } from "@material-ui/styles";
import { func } from "prop-types";
import { Typography, Grid, TextField, Button } from "@material-ui/core";
import { getIndexesAPI, updateIndex, saveNewIndex } from "request/settingsAPI";

const trimesters = ["T1", "T2", "T3", "T4"];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
}));

const IndexInsee = () => {
  const classes = useStyles();
  const currentDate = new Date().toLocaleString("default", {
    year: "numeric",
  });
  const years = [
    Number(currentDate),
    Number(currentDate) - 1,
    Number(currentDate) - 2,
  ];
  const [indexes, setIndexes] = useState({});

  function getIndexes() {
    getIndexesAPI((response) => {
      setIndexes(response);
    });
  }

  function handleSave(){

  }

  useEffect(() => {
    getIndexes();
  }, []);

  return (
    <div className={classes.root}>
      <Button variant="outlined">Sauvegarder</Button>
      <Grid container spacing={2}>
        {years.map((year) => (
          <>
            <Grid item lg={12} md={12} xs={12} className={classes.title}>
              <Typography variant="h5">{year}</Typography>
            </Grid>
            {trimesters.map((value) => (
              <Grid item lg={3} md={3} xs={3} className={classes.title}>
                <TextField
                  name={year + "_" + value}
                  size="small"
                  fullWidth
                  label={value}
                  type="number"
                  variant="outlined"
                />
              </Grid>
            ))}
          </>
        ))}
      </Grid>
    </div>
  );
};

export default IndexInsee;
