import React, { useState, useEffect } from "react";
import { makeStyles, withStyles } from "@material-ui/styles";
import { func } from "prop-types";
import {
  Typography,
  Grid,
  TextField,
  Button,
  IconButton,
} from "@material-ui/core";
import { getTemplateByIdAPI, updateTemplateAPI } from "request/settingsAPI";
import { useParams } from "react-router-dom";
import { InfoRounded, Report } from "@material-ui/icons";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(6),
  },
  button: {
    marginBottom: 30,
  },
  title: {
    marginBottom: 10,
  },
  cellCenter: {
    display: "flex",
    alignItems: "center",
  },
}));

const matching = [
  "Nom du locataire : [name_rental]",
  "CivilitÃ© du locataire : [civility]",
  "Adresse complete : [complete_address]",
  "Ville du logement : [city]",
];

const TemplateEdit = () => {
  const classes = useStyles();
  let { idTemplate } = useParams();
  const [template, setTemplate] = useState({});

  function getTemplate() {
    console.log("idTemplate", idTemplate);
    getTemplateByIdAPI(idTemplate, (response) => {
      console.log("response", response);
    });
  }

  function handleChange() {}

  useEffect(() => {
    getTemplate();
  }, []);

  return (
    <div className={classes.root}>
      <Grid container spacing={10}>
        <Grid item lg={12} md={12} xs={12}>
          <Typography variant="subtitle1">
            Ici tu peux modifier le texte de tes fichiers Ã  exporter. Pour
            ajouter un champs variable tel que l'addresse ou le nom de la
            personne rÃ©fÃ¨re toi Ã  la liste ci dessous.
          </Typography>
          {matching.map((value) => (
            <Typography variant="subtitle1">{value}</Typography>
          ))}
        </Grid>

        <Grid item lg={6} md={6} xs={6}>
          <Grid container>
            <Grid item lg={12} md={12} xs={12} className={classes.cellCenter}>
              <TextField
                name="textStart"
                size="small"
                multiline
                rows={8}
                fullWidth
                label="Text start"
                type="text"
                onChange={handleChange}
                variant="outlined"
              />
              <IconButton>
                <InfoRounded />
              </IconButton>
            </Grid>
            <Grid item lg={12} md={12} xs={12} className={classes.cellCenter}>
              <TextField
                name="textBody"
                size="small"
                multiline
                rows={8}
                fullWidth
                label="Text body"
                type="text"
                onChange={handleChange}
                variant="outlined"
              />
              <IconButton>
                <InfoRounded />
              </IconButton>
            </Grid>
            <Grid item lg={12} md={12} xs={12} className={classes.cellCenter}>
              <TextField
                name="textEnd"
                size="small"
                multiline
                rows={8}
                fullWidth
                label="Text End"
                type="text"
                onChange={handleChange}
                variant="outlined"
              />
              <IconButton>
                <InfoRounded />
              </IconButton>
            </Grid>
          </Grid>
        </Grid>
        <Grid item lg={6} md={6} xs={6}>
          Preview
        </Grid>
      </Grid>
    </div>
  );
};

export default TemplateEdit;
