import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { func } from "prop-types";
import { Typography, Button } from "@material-ui/core";
import html2canvas from "html2canvas";
import Template from "./Template"
import jsPDF from "jspdf";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
    margin : theme.spacing(2)
  },
  content: {
    marginTop: theme.spacing(2),
  },
  destinataire:{
      color: theme.palette.primary.main
  },
  expediteur:{
    textAlign: "right" 
}, page:{
    backgroundColor: '#fff000',
        width: '210mm',
        height: '297mm',
        padding: 30,
        position: absolute,
         opacity: 0,
          pointerEvents:none
}
}));

const Receipt = () => {
  const classes = useStyles();

  const text = "<div> <div>HELLO </div> <div style={text-align:'right'}> WORLD </div></div>"

  function createPDF() {
    console.log("create PDF")

        const input = document.createElement('<Template/>');
        if (input != null){
            html2canvas(input)
            .then((canvas) => {
                const image = canvas.toDataURL('image/png');
                const pdf = jsPDF("p", "mm", "a4");
                const width = pdf.internal.pageSize.getWidth();
                const height = pdf.internal.pageSize.getHeight();


            pdf.addImage(image,0,0);
            pdf.save("download.pdf");

            });
        }
  }


  return (
    <div className={classes.root}>
        <h1>HELLO ON VA FAIRE DES QUITTANCES</h1>
        <Button className={classes.root} variant="contained" onClick={createPDF}>GET PDF</Button>
        <div id="divToPrint" className={classes.page} hidden={true}>
            <div className={classes.destinataire}>Note: Here the dimensions of div are same as A4</div> 
            <div className={classes.expediteur}>You Can add any component here</div>
        </div>
    </div>
  );
};

export default Receipt;
