import React, { useState, useEffect } from "react";
import { makeStyles, withStyles } from "@material-ui/styles";
import { func } from "prop-types";
import { Typography, Grid, TextField, Button } from "@material-ui/core";
import {
  getIndexesAPI,
  updateIndexesAPI,
  saveNewIndex,
} from "request/settingsAPI";

const trimesters = ["T1", "T2", "T3", "T4"];

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  button: {
    marginBottom: 30,
  },
  titleCell: {
    marginBottom: 10,
  }
}));

const IndexInsee = () => {
  const classes = useStyles();
  const currentDate = new Date().toLocaleString("default", {
    year: "numeric",
  });
  const years = [
    Number(currentDate),
    Number(currentDate) - 1,
    Number(currentDate) - 2,
  ];
  const [indexes, setIndexes] = useState({});

  function getIndexes() {
    getIndexesAPI((response) => {
      setIndexes(response);
    });
  }

  function handleChange(event) {
    event.persist();

    setIndexes((prev) => ({
      ...prev,
      [event.target.name]: event.target.value,
    }));
  }

  function handleSave() {
    updateIndexesAPI(indexes);
  }

  useEffect(() => {
    getIndexes();
  }, []);

  return (
    <div className={classes.root}>
      <Button
        className={classes.button}
        variant="outlined"
        onClick={handleSave}
      >
        Sauvegarder
      </Button>
      <Grid container spacing={2}>
        {years.map((year) => (
          <>
            <Grid item lg={4} md={4} xs={4} className={classes.title}>
              <Typography variant="h5" className={classes.titleCell}>{year}</Typography>
              <Grid container spacing={2}>
                {trimesters.map((value) => (
                  <Grid item lg={3} md={3} xs={3} className={classes.title}>
                    <TextField
                      name={year + "_" + value}
                      size="small"
                      fullWidth
                      label={value}
                      value={indexes[year + "_" + value] || ""}
                      type="number"
                      onChange={handleChange}
                      variant="outlined"
                    />
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </>
        ))}
      </Grid>
    </div>
  );
};

export default IndexInsee;
