import Firebase from "firebase";

export const saveNewIndex = (value, name) => {
  Firebase.database()
    .ref("indexes/" + name)
    .set({
      value,
    });
};

export const updateIndex = (name, value) => {
  Firebase.database()
    .ref("indexes/" + name)
    .set(value);
  console.log("DATA SAVED");
};

export const getIndexes = (callback) => {
  let ref = Firebase.database().ref("/indexes");
  ref.on("value", (snapshot) => {
    const data = snapshot.val();
    callback(data);
  });
};
